CREATE OR REPLACE PROCEDURE pls_baca_ptu_v91_fatura_nf () AS $body$
BEGIN

-- Vai buscar todas as NF do PTU, levantar qual foi a fatura que elas pertencem, e gravar o numero da fatura
update	ptu_nota_fiscal	a
set	a.nr_seq_fatura = (	SELECT	max(x.nr_seq_fatura)
				from	ptu_nota_cobranca	x
				where	x.nr_sequencia		= a.nr_seq_nota_cobr)
where	coalesce(a.nr_seq_fatura::text, '') = '';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baca_ptu_v91_fatura_nf () FROM PUBLIC;

