CREATE OR REPLACE PROCEDURE pls_baca_reaj_retro_ind () AS $body$
DECLARE


nr_seq_reajuste_w	pls_reajuste.nr_sequencia%type;
ds_mes_reajuste_w	varchar(2);
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
