C01 CURSOR FOR
	SELECT	nr_sequencia,
		to_char(dt_reajuste,'mm') ds_mes_reajuste,
		cd_estabelecimento
	from	pls_reajuste
	where	ie_tipo_reajuste = 'I'
	and	dt_reajuste between to_date('01/05/2014') and to_date('31/07/2014');


