	where	nr_seq_reajuste = nr_seq_reajuste_w;

	if (qt_registros_w = 0) then
		if (ds_mes_reajuste_w = '07') then
			insert	into	pls_reajuste_cobr_retro(	nr_sequencia, nr_seq_reajuste, cd_estabelecimento,
