					clock_timestamp(), 'Tasy', clock_timestamp(), 'Tasy',
					to_date('01/08/2014'), to_date('01/06/2014'));

			insert	into	pls_reajuste_cobr_retro(	nr_sequencia, nr_seq_reajuste, cd_estabelecimento,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					dt_referencia_mensalidade, dt_referencia_reajuste)
				values (	nextval('pls_reajuste_cobr_retro_seq'), nr_seq_reajuste_w, cd_estabelecimento_w,
					clock_timestamp(), 'Tasy', clock_timestamp(), 'Tasy',
					to_date('01/09/2014'), to_date('01/07/2014'));
		elsif (ds_mes_reajuste_w = '05') then
			insert	into	pls_reajuste_cobr_retro(	nr_sequencia, nr_seq_reajuste, cd_estabelecimento,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					dt_referencia_mensalidade, dt_referencia_reajuste)
				values (	nextval('pls_reajuste_cobr_retro_seq'), nr_seq_reajuste_w, cd_estabelecimento_w,
					clock_timestamp(), 'Tasy', clock_timestamp(), 'Tasy',
					to_date('01/08/2014'), to_date('01/05/2014'));

			insert	into	pls_reajuste_cobr_retro(	nr_sequencia, nr_seq_reajuste, cd_estabelecimento,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
