CREATE OR REPLACE PROCEDURE pls_baca_regras_oc_partic () AS $body$
DECLARE


nr_seq_sub_regra_partic_w	bigint;
nr_seq_regra_w			bigint;
nr_seq_regra_grupo_w		bigint;
qt_existe_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_regra
	from	pls_itens_regra_partic
	where	coalesce(nr_seq_grupo_regra::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_sub_regra_partic_w,
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_grupo_w
	from	pls_oc_regra_grupo_partic
	where	nr_seq_regra_partic = nr_seq_regra_w;

	if (coalesce(nr_seq_regra_grupo_w,0) = 0) then

		select	nextval('pls_oc_regra_grupo_partic_seq')
		into STRICT	nr_seq_regra_grupo_w
		;

		insert into pls_oc_regra_grupo_partic(nr_sequencia, nr_seq_regra_partic, ds_grupo,
			 dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			 nm_usuario_nrec)
		values (nr_seq_regra_grupo_w, nr_seq_regra_w, 'Grupo/Situação 1',
			 clock_timestamp(), 'Tasy', clock_timestamp(),
			 'Tasy');
	end if;

	update	pls_itens_regra_partic
	set	nr_seq_grupo_regra = nr_seq_regra_grupo_w
	where	nr_sequencia = nr_seq_sub_regra_partic_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
