CREATE OR REPLACE PROCEDURE pls_baca_regra_reajuste ( nm_usuario_p text) AS $body$
DECLARE


ds_oficio_ans_w			varchar(255);
nr_protocolo_ans_w		varchar(60);
dt_autorizacao_ans_w		timestamp;
ds_observacao_w			varchar(2000);
dt_periodo_inicial_w		timestamp;
dt_periodo_final_w		timestamp;
cd_estabelecimento_w		smallint;
nr_seq_regra_w			bigint;
tx_reajuste_w			double precision;

C01 CURSOR FOR
	SELECT	distinct
		ds_oficio_ans,
		nr_protocolo_ans,
		dt_autorizacao_ans,
		ds_observacao,
		dt_periodo_inicial,
		dt_periodo_final,
		cd_estabelecimento,
		tx_reajuste
	from	pls_reajuste
	where	ie_tipo_reajuste	= 'I'
	and	ie_status <> '1';


BEGIN

open C01;
loop
fetch C01 into
	ds_oficio_ans_w,
	nr_protocolo_ans_w,
	dt_autorizacao_ans_w,
	ds_observacao_w,
	dt_periodo_inicial_w,
	dt_periodo_final_w,
	cd_estabelecimento_w,
	tx_reajuste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('pls_regra_reajuste_seq')
	into STRICT	nr_seq_regra_w
	;

	insert into pls_regra_reajuste(	nr_sequencia,
					cd_estabelecimento,
					dt_periodo_inicial,
					dt_periodo_final,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ds_oficio_ans,
					nr_protocolo_ans,
					dt_autorizacao_ans,
					ds_observacao,
					tx_reajuste,
					ie_situacao)
				values (	nr_seq_regra_w,
					cd_estabelecimento_w,
					dt_periodo_inicial_w,
					dt_periodo_final_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					ds_oficio_ans_w,
					nr_protocolo_ans_w,
					dt_autorizacao_ans_w,
					ds_observacao_w,
					tx_reajuste_w,
					'A');

	update	pls_reajuste
	set	nr_seq_regra	= nr_seq_regra_w
	where	coalesce(ds_oficio_ans,'0')		= coalesce(ds_oficio_ans_w,'0')
	and	coalesce(nr_protocolo_ans,'0')	= coalesce(nr_protocolo_ans_w,'0')
	and	coalesce(dt_autorizacao_ans,clock_timestamp())	= coalesce(dt_autorizacao_ans_w,clock_timestamp())
	and	coalesce(ds_observacao,'0')		= coalesce(ds_observacao_w,'0')
	and	dt_periodo_inicial		= dt_periodo_inicial_w
	and	dt_periodo_final		= dt_periodo_final_w;

	end;
end loop;
close C01;

commit;

end;
$body$
