SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_seq_mens_sca () AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		c.nr_seq_mensalidade 
	from	pls_mensalidade_sca a, 
		pls_mensalidade_seg_item b, 
		pls_mensalidade_segurado c 
	where	b.nr_sequencia	= a.nr_seq_item_mens 
	and	c.nr_sequencia	= b.nr_seq_mensalidade_seg 
	and	coalesce(a.nr_seq_mensalidade::text, '') = '';

nr_seq_sca_w		pls_util_cta_pck.t_number_table;
nr_seq_mensalidade_w	pls_util_cta_pck.t_number_table;
x bigint;

BEGIN 
x	:= 0;
for r_c01_w in C01 loop 
	begin 
	nr_seq_sca_w(x)	:= r_c01_w.nr_sequencia;
	nr_seq_mensalidade_w(x)	:= r_c01_w.nr_seq_mensalidade;
	 
	if (x = 500) then 
		forall i in nr_seq_sca_w.first..nr_seq_sca_w.last 
			update	pls_mensalidade_sca 
			set	nr_seq_mensalidade	= nr_seq_mensalidade_w(i) 
			where	nr_sequencia	= nr_seq_sca_w(i);
		commit;
		 
		nr_seq_sca_w.delete;
		nr_seq_mensalidade_w.delete;
		x := 0;
	else 
		x	:= x + 1;
	end if;
	 
	end;
end loop;
 
if (nr_seq_sca_w.count > 0) then 
	forall i in nr_seq_sca_w.first..nr_seq_sca_w.last 
		update	pls_mensalidade_sca 
		set	nr_seq_mensalidade	= nr_seq_mensalidade_w(i) 
		where	nr_sequencia	= nr_seq_sca_w(i);
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
