SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_status_lote_comissao () AS $body$
DECLARE


qt_registro_w		integer;
nr_seq_lote_w		pls_lote_comissao.nr_sequencia%type;
dt_geracao_w		pls_lote_comissao.dt_geracao%type;
dt_fechamento_lote_w	pls_lote_comissao.dt_fechamento_lote%type;
dt_geracao_titulo_w	pls_lote_comissao.dt_geracao_titulo%type;
ie_status_lote_w	pls_lote_comissao.ie_status%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_geracao,
		dt_fechamento_lote,
		dt_geracao_titulo
	from	pls_lote_comissao
	where	coalesce(ie_status::text, '') = '';


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	pls_lote_comissao
where	coalesce(ie_status::text, '') = '';

if (qt_registro_w > 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_lote_w,
		dt_geracao_w,
		dt_fechamento_lote_w,
		dt_geracao_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (dt_geracao_titulo_w IS NOT NULL AND dt_geracao_titulo_w::text <> '') then
			ie_status_lote_w := 'E'; -- Encerrado
		elsif (dt_fechamento_lote_w IS NOT NULL AND dt_fechamento_lote_w::text <> '') then
			ie_status_lote_w := 'F'; -- Fechado
		elsif (dt_geracao_w IS NOT NULL AND dt_geracao_w::text <> '') then
			ie_status_lote_w := 'G'; -- Gerado
		else
			ie_status_lote_w := 'U'; -- Usuário (aguardando ação)
		end if;

		update	pls_lote_comissao
		set	ie_status = ie_status_lote_w
		where	nr_sequencia = nr_seq_lote_w;
		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
