CREATE OR REPLACE PROCEDURE pls_baca_status_mov_portal () AS $body$
DECLARE

 
nr_seq_mov_w		pls_inclusao_beneficiario.nr_sequencia%type;
dt_rejeicao_w		pls_inclusao_beneficiario.dt_rejeicao%type;
dt_efetivacao_w		pls_inclusao_beneficiario.dt_efetivacao%type;
ie_status_mov_w		pls_inclusao_beneficiario.ie_status_mov%type;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		dt_rejeicao, 
		dt_efetivacao 
	from	pls_inclusao_beneficiario;


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_mov_w, 
	dt_rejeicao_w, 
	dt_efetivacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (dt_efetivacao_w IS NOT NULL AND dt_efetivacao_w::text <> '') then 
		ie_status_mov_w	:= '2';
	elsif (dt_rejeicao_w IS NOT NULL AND dt_rejeicao_w::text <> '') then 
		ie_status_mov_w	:= '3';
	else 
		ie_status_mov_w	:= '1';
	end if;
	 
	update	pls_inclusao_beneficiario 
	set	ie_status_mov	= ie_status_mov_w 
	where	nr_sequencia	= nr_seq_mov_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
