CREATE OR REPLACE PROCEDURE pls_baca_transfere_vl_param ( cd_funcao_p funcao.cd_funcao%type, nr_seq_funcao_param_p funcao_parametro.nr_sequencia%type, nm_tabela_p tabela_sistema.nm_tabela%type, nm_atributo_p tabela_atributo.nm_atributo%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Baca utilizada para transferir o valor 'Padrão cliente' de um parâmetro do SHIFT+F5 para alguma tabela.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ x ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_parametro_w		funcao_parametro.vl_parametro%type;
qt_registro_w		bigint;
ds_sql_w		varchar(2000);
ds_parametro_w		varchar(255);


BEGIN
begin
	select	vl_parametro
	into STRICT	vl_parametro_w
	from	funcao_parametro
	where	nr_sequencia	= nr_seq_funcao_param_p
	and	cd_funcao	= cd_funcao_p;
exception
when others then
	vl_parametro_w	:= null;
end;

if (vl_parametro_w IS NOT NULL AND vl_parametro_w::text <> '') then
	select	count(1)
	into STRICT	qt_registro_w
	from	user_tab_columns
	where	table_name 	= upper(nm_tabela_p)
	and	column_name	= upper(nm_atributo_p);

	if (qt_registro_w > 0) then
		ds_sql_w := 'UPDATE ' || nm_tabela_p;
		ds_sql_w := ds_sql_w || ' SET ' || nm_atributo_p || ' = :' || nm_atributo_p;
		ds_parametro_w := nm_atributo_p || '=' || vl_parametro_w;

		CALL exec_sql_dinamico_bv('',ds_sql_w,ds_parametro_w);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
