SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_versao_tiss () AS $body$
DECLARE


ds_versao_tiss_w		varchar(15);
qt_registros_w			bigint;
nr_seq_pls_versao_tiss_w	bigint;



C01 CURSOR FOR
	SELECT 	nr_sequencia
	from	pls_outorgante_webservice;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_versao_tiss;

if ( qt_registros_w = 0) then

	for cr_C01 in C01 loop
		begin

		select	max(ds_versao_tiss)
		into STRICT	ds_versao_tiss_w
		from	pls_regra_webservice
		where	nr_seq_out_webservice   = cr_c01.nr_sequencia;


		select	max(nr_sequencia)
		into STRICT	nr_seq_pls_versao_tiss_w
		from	pls_versao_tiss
		where	cd_versao_tiss = ds_versao_tiss_w;

		if (coalesce(nr_seq_pls_versao_tiss_w::text, '') = '') then
			select	nextval('pls_versao_tiss_seq')
			into STRICT	nr_seq_pls_versao_tiss_w
			;

			insert	into	pls_versao_tiss(nr_sequencia, dt_inicio_vigencia, cd_versao_tiss,
							dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
							nm_usuario_nrec, dt_fim_vigencia)
						values (nr_seq_pls_versao_tiss_w, to_date('01/01/2014', 'dd/mm/yyyy'), ds_versao_tiss_w,
							clock_timestamp(), 'BACA', clock_timestamp(),
							'BACA', null);
		end if;

		update  pls_outorgante_webservice
		set	nr_seq_versao_tiss = nr_seq_pls_versao_tiss_w
		where	nr_sequencia   = cr_c01.nr_sequencia;

		end;
	end loop;
end if;


--Verifica se existe a vers√£o TISS utilizada no antigo IE_VERSAO da PLS_PARAMETROS
select  max(ie_versao)
into STRICT	ds_versao_tiss_w
from	pls_parametros;

if (ds_versao_tiss_w IS NOT NULL AND ds_versao_tiss_w::text <> '') then

	select	count(1)
	into STRICT	qt_registros_w
	from	pls_versao_tiss
	where 	cd_versao_tiss = ds_versao_tiss_w;

	if ( qt_registros_w = 0 ) then
		insert	into pls_versao_tiss(
			nr_sequencia, dt_inicio_vigencia, cd_versao_tiss,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec, dt_fim_vigencia)
		values (nr_seq_pls_versao_tiss_w, to_date('01/01/2014', 'dd/mm/yyyy'), ds_versao_tiss_w,
			clock_timestamp(), 'BACA', clock_timestamp(),
			'BACA', null);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
