SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_vl_item_repasse () AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_mensalidade_seg_item
	from	pls_repasse_mens_item
	where	coalesce(vl_item::text, '') = '';

nr_seq_repasse_item_w	bigint;
nr_seq_mens_item_w	bigint;
vl_item_w		double precision;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_repasse_item_w,
	nr_seq_mens_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(vl_item)
	into STRICT	vl_item_w
	from	pls_mensalidade_seg_item
	where	nr_sequencia	= nr_seq_mens_item_w;

	update	pls_repasse_mens_item
	set	vl_item		= vl_item_w
	where	nr_sequencia	= nr_seq_repasse_item_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
