SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baca_vl_uni_ptu () AS $body$
DECLARE


nr_seq_conta_mat_w	bigint;
vl_unitario_imp_w	double precision;
vl_material_imp_w	double precision;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		coalesce(a.vl_unitario_imp,0),
		coalesce(a.vl_material_imp,0)
	from	pls_conta_mat a,
		pls_conta b
	where	a.nr_seq_conta = b.nr_sequencia
	and	b.ie_origem_conta = 'A'
	and	coalesce(a.vl_unitario_imp,0) > coalesce(a.vl_material_imp,0)
	order by 1;

C02 CURSOR FOR
	SELECT	a.nr_sequencia,
		coalesce(a.vl_unitario_apres,0),
		coalesce(a.vl_total_apres,0)
	from	w_pls_resumo_conta a,
		pls_analise_conta b
	where	a.nr_seq_analise  = b.nr_sequencia
	and	b.ie_origem_conta = 'A'
	and	coalesce(a.vl_unitario_apres,0) > coalesce(a.vl_total_apres,0)
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_mat_w,
	vl_unitario_imp_w,
	vl_material_imp_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_conta_mat
	set	vl_unitario_imp = vl_material_imp_w,
		vl_material_imp = vl_unitario_imp_w
	where	nr_sequencia 	= nr_seq_conta_mat_w;

	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_conta_mat_w,
	vl_unitario_imp_w,
	vl_material_imp_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	update	w_pls_resumo_conta
	set	vl_unitario_apres = vl_material_imp_w,
		vl_total_apres 	  = vl_unitario_imp_w
	where	nr_sequencia 	  = nr_seq_conta_mat_w;

	end;
end loop;
close C02;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
