CREATE OR REPLACE PROCEDURE pls_baixar_adiant_pago_vend ( nr_titulo_p bigint, nr_seq_repasse_p bigint, nr_seq_comissao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
vl_saldo_adiantamento_w		adiantamento_pago.vl_saldo%type;
nr_adiantamento_w		adiantamento_pago.nr_adiantamento%type;
nr_seq_tipo_w			adiantamento_pago.nr_seq_tipo%type;
vl_saldo_titulo_w		titulo_pagar.vl_saldo_titulo%type;
nr_sequencia_w			titulo_pagar_adiant.nr_sequencia%type;
nr_seq_trans_fin_w		tipo_adiant_pago.nr_seq_trans_fin%type;
nr_seq_canal_venda_w		pls_vendedor.nr_sequencia%type;
vl_baixar_w			double precision;
dt_referencia_w			pls_repasse_vend.dt_referencia%type;
dt_movimento_w			pls_repasse_vend.dt_movimento%type;
dt_movimento_fim_w		pls_repasse_vend.dt_movimento_fim%type;

C01 CURSOR FOR 
	SELECT	nr_adiantamento, 
		nr_seq_tipo, 
		vl_saldo 
	from	adiantamento_pago 
	where	nr_seq_canal_venda = nr_seq_canal_venda_w 
	and	dt_adiantamento between dt_movimento_w and dt_movimento_fim_w 
	and	vl_saldo > 0 
	order by dt_adiantamento;


BEGIN 
 
if (nr_seq_repasse_p IS NOT NULL AND nr_seq_repasse_p::text <> '') then -- OPS - Gestão de Vendedores e Comissões	 
	select	nr_seq_vendedor, 
		dt_referencia, 
		trunc(dt_movimento,'dd'), 
		dt_movimento_fim 
	into STRICT	nr_seq_canal_venda_w, 
		dt_referencia_w, 
		dt_movimento_w, 
		dt_movimento_fim_w 
	from	pls_repasse_vend 
	where	nr_sequencia = nr_seq_repasse_p;
	 
	if (dt_movimento_fim_w IS NOT NULL AND dt_movimento_fim_w::text <> '') then 
		dt_movimento_fim_w	:= fim_dia(dt_movimento_fim_w);
	end if;
else -- OPS - Controle de Comissões de Venda 
	select	b.nr_seq_canal_venda, 
		a.dt_referencia 
	into STRICT	nr_seq_canal_venda_w, 
		dt_referencia_w 
	from	pls_comissao		b, 
		pls_lote_comissao	a 
	where	a.nr_sequencia = b.nr_seq_lote 
	and	b.nr_sequencia = nr_seq_comissao_p;
end if;
 
dt_movimento_w		:= coalesce(dt_movimento_w, trunc(dt_referencia_w,'month'));
dt_movimento_fim_w	:= coalesce(dt_movimento_fim_w, fim_dia(last_day(dt_referencia_w)));
 
open C01;
loop 
fetch C01 into 
	nr_adiantamento_w, 
	nr_seq_tipo_w, 
	vl_saldo_adiantamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	vl_saldo_titulo 
	into STRICT	vl_saldo_titulo_w 
	from	titulo_pagar 
	where	nr_titulo = nr_titulo_p;
	 
	if (vl_saldo_titulo_w > 0) then 
		if (vl_saldo_adiantamento_w < vl_saldo_titulo_w) then 
			vl_baixar_w	:= vl_saldo_adiantamento_w;
		else 
			vl_baixar_w	:= vl_saldo_titulo_w;
		end if;
		 
		select	max(nr_seq_trans_fin) 
		into STRICT	nr_seq_trans_fin_w 
		from	tipo_adiant_pago 
		where	nr_sequencia	= nr_seq_tipo_w;
		 
		select	coalesce(max(nr_sequencia),0) + 1 
		into STRICT	nr_sequencia_w 
		from	titulo_pagar_adiant 
		where	nr_titulo	= nr_titulo_p;
		 
		insert	into	titulo_pagar_adiant(	nr_titulo, nr_sequencia, nr_adiantamento, 
				vl_adiantamento, dt_atualizacao, nm_usuario, 
				nr_seq_trans_fin, ds_observacao, dt_contabil) 
			values (	nr_titulo_p, nr_sequencia_w, nr_adiantamento_w, 
				vl_baixar_w, clock_timestamp(), nm_usuario_p, 
				nr_seq_trans_fin_w, '', dt_referencia_w);
		 
		CALL atualiza_adiantamento_pago(nr_titulo_p, nr_adiantamento_w, vl_baixar_w,	null, nm_usuario_p, 'I');
	end if;
	end;
end loop;
close C01;
 
--commit; 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baixar_adiant_pago_vend ( nr_titulo_p bigint, nr_seq_repasse_p bigint, nr_seq_comissao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

