CREATE OR REPLACE PROCEDURE pls_baixar_mensalidade ( nr_seq_lote_p bigint, nm_usuario_p text, dt_baixa_p timestamp, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_mensalidade_w		bigint;
nr_titulo_w			bigint;
vl_saldo_titulo_w		double precision;
nr_seq_trans_fin_baixa_w	bigint;
cd_tipo_recebimento_w		integer;
nr_seq_conta_banco_w		bigint;
nr_seq_baixa_w			bigint;
nr_seq_trans_financ_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		substr(pls_obter_titulo_mensalidade(nr_sequencia,null),1,255) 
	from	pls_mensalidade 
	where	nr_seq_lote = nr_seq_lote_p 
	and	coalesce(ie_cancelamento,'N') not in ('C','E') 
	and	substr(pls_obter_titulo_mensalidade(nr_sequencia,null),1,255) is not null;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_mensalidade_w, 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	vl_saldo_titulo, 
		nr_seq_conta_banco 
	into STRICT	vl_saldo_titulo_w, 
		nr_seq_conta_banco_w 
	from	titulo_receber 
	where	nr_titulo = nr_titulo_w;
	 
	if (vl_saldo_titulo_w <= 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(185647);
		--'Título já foi baixado!'); 
	end if;
	 
	select	nr_seq_trans_fin_baixa, 
		cd_tipo_recebimento 
	into STRICT	nr_seq_trans_financ_w, 
		cd_tipo_recebimento_w 
	from	pls_parametros 
	where	cd_estabelecimento = cd_estabelecimento_p;
	 
	if (coalesce(cd_tipo_recebimento_w::text, '') = '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 243963, null);
	end if;
	 
	CALL Baixa_Titulo_Receber(	cd_estabelecimento_p,	--cd_estabelecimento_p 
				cd_tipo_recebimento_w,	--cd_tipo_recebimento_p 
				nr_titulo_w,		--nr_titulo_p 
				nr_seq_trans_financ_w,	--nr_seq_trans_financ_p 
				vl_saldo_titulo_w, 	--vl_baixa_p 
				dt_baixa_p, 		--dt_recebimento_p 
				nm_usuario_p,		--nm_usuario_p 
				0, 			--vl_glosa_p 
				null,			--nr_bordero_p 
				null, 
				0, 
				0);			--vl_perdas_p 
	/* Paulo/Francisco/Edgar - Não é necessário atualizar o banco em baixas de primeira mensalidade 
	select	max(nr_sequencia) 
	into	nr_seq_baixa_w 
	from	titulo_receber_liq 
	where	nr_titulo = nr_titulo_w; 
	 
	update	titulo_receber_liq 
	set	nr_seq_conta_banco = nr_seq_conta_banco_w 
	where	nr_titulo	  = nr_titulo_w 
	and	nr_sequencia 	  = nr_seq_baixa_w; 
	 
	 
	Gerar_movto_tit_baixa(nr_titulo_w, nr_seq_baixa_w, 'R', nm_usuario_p, 'S'); 
	*/
	CALL Atualizar_Saldo_Tit_Rec(nr_titulo_w,nm_usuario_p);
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
