SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baixar_titulos_eventos ( nr_seq_pag_item_p bigint, nr_tit_pagar_p bigint, nr_tit_receber_p bigint, vl_movimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

	 
nr_seq_baixa_w		bigint;			
nr_seq_lote_w		bigint;
cd_moeda_w		integer;
cd_tipo_baixa_enc_tit_w	integer;
cd_tipo_receb_enc_tit_w	integer;
vl_movimento_w		double precision;
			
			 

BEGIN 
vl_movimento_w	:= vl_movimento_p;
 
if (nr_tit_pagar_p IS NOT NULL AND nr_tit_pagar_p::text <> '') then 
	select	coalesce(max(cd_moeda_padrao),1) 
	into STRICT	cd_moeda_w 
	from	parametros_contas_pagar;
	 
	select	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_seq_baixa_w 
	from	titulo_pagar_baixa 
	where	nr_titulo = nr_tit_pagar_p;
	 
	select	max(cd_tipo_baixa_enc_tit) 
	into STRICT	cd_tipo_baixa_enc_tit_w 
	from	pls_parametro_pagamento;
	 
	if (coalesce(cd_tipo_baixa_enc_tit_w::text, '') = '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 267292, null ); /* É necessário cadastrar o parâmetro Tipo baixa pagar encontro.' + Chr(13) + 'Cadastrar em OPS - Gestão de Operadoras -> Parâmetros da OPS -> Pagamento produção */
	end if;
	 
	select	max(a.nr_seq_lote) 
	into STRICT	nr_seq_lote_w 
	from	pls_pagamento_item b, 
		pls_pagamento_prestador a 
	where	a.nr_sequencia = b.nr_seq_pagamento 
	and	b.nr_sequencia = nr_seq_pag_item_p;
	 
	if (vl_movimento_p < 0) then 
		vl_movimento_w	:= vl_movimento_w * -1;
	end if;
 
	insert into titulo_pagar_baixa(nr_titulo, 
		nr_sequencia, 
		nm_usuario, 
		dt_atualizacao, 
		nr_seq_pag_item, 
		cd_moeda, 
		cd_tipo_baixa, 
		ie_acao, 
		dt_baixa, 
		vl_baixa, 
		vl_descontos, 
		vl_devolucao, 
		vl_juros, 
		vl_multa, 
		vl_pago, 
		vl_inss, 
		vl_ir, 
		vl_glosa, 
		vl_outras_deducoes, 
		vl_outros_acrescimos, 
		ds_observacao) 
	values (nr_tit_pagar_p, 
		nr_seq_baixa_w, 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_seq_pag_item_p, 
		cd_moeda_w, 
		cd_tipo_baixa_enc_tit_w, 
		'I', 
		clock_timestamp(), 
		vl_movimento_w, 
		0, 
		0, 
		0, 
		0, 
		vl_movimento_w, 
		0, 
		0, 
		0, 
		0, 
		0, 
		'Baixa gerada através do lote, '|| nr_seq_lote_w||', de pagamento de produção médica.');
	 
	CALL atualizar_saldo_tit_pagar(nr_tit_pagar_p,nm_usuario_p);
	CALL Gerar_W_Tit_Pag_imposto(nr_tit_pagar_p, nm_usuario_p);
		 
elsif (nr_tit_receber_p IS NOT NULL AND nr_tit_receber_p::text <> '') then 
	select	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_seq_baixa_w 
	from	titulo_receber_liq 
	where	nr_titulo	= nr_tit_receber_p;
	 
	select	coalesce(max(cd_moeda_padrao),1) 
	into STRICT	cd_moeda_w 
	from	parametro_contas_receber;
	 
	begin 
	select	max(cd_tipo_receb_enc_tit) 
	into STRICT	cd_tipo_receb_enc_tit_w 
	from	pls_parametro_pagamento;	
	exception 
		when others then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 267294, null ); /* É necessário cadastrar o parâmetro Tipo baixa receber encontro.' + Chr(13) + 'Cadastrar em OPS - Gestão de Operadoras -> Parâmetros da OPS -> Pagamento produção. */
	end;
	 
	select	max(a.nr_seq_lote) 
	into STRICT	nr_seq_lote_w 
	from	pls_pagamento_item b, 
		pls_pagamento_prestador a 
	where	a.nr_sequencia = b.nr_seq_pagamento 
	and	b.nr_sequencia = nr_seq_pag_item_p;
	 
	if (vl_movimento_p < 0) then 
		vl_movimento_w	:= vl_movimento_w * -1;
	end if;
	 
	insert into titulo_receber_liq(nr_titulo, 
		nr_sequencia, 
		nm_usuario, 
		dt_atualizacao, 
		nr_seq_pag_item, 
		dt_recebimento, 
		vl_recebido, 
		vl_descontos, 
		vl_juros, 
		vl_multa, 
		cd_moeda, 
		cd_tipo_recebimento, 
		ie_acao, 
		vl_rec_maior, 
		vl_glosa, 
		vl_nota_credito, 
		vl_outros_acrescimos, 
		vl_perdas, 
		ie_lib_caixa, 
		ds_observacao) 
	values (nr_tit_receber_p, 
		nr_seq_baixa_w, 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_seq_pag_item_p, 
		clock_timestamp(), 
		vl_movimento_w, 
		0, 
		0, 
		0, 
		cd_moeda_w, 
		cd_tipo_receb_enc_tit_w, 
		'I', 
		0, 
		0, 
		0, 
		0, 
		0, 
		'S', 
		'Baixa gerada através do lote, '|| nr_seq_lote_w||', de pagamento de produção médica.');
		 
	CALL atualizar_saldo_tit_rec(nr_tit_receber_p,nm_usuario_p);
end if;
 
/* Não pode ter commit */
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baixar_titulos_eventos ( nr_seq_pag_item_p bigint, nr_tit_pagar_p bigint, nr_tit_receber_p bigint, vl_movimento_p bigint, nm_usuario_p text) FROM PUBLIC;

