SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_baixar_tit_pag_rec_propr ( nr_titulo_pagar_p titulo_pagar.nr_titulo%type, nr_seq_pag_prestador_p pls_pagamento_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type ) AS $body$
DECLARE


nr_seq_trans_fin_baixa_w	titulo_pagar.nr_seq_trans_fin_baixa%type;
cd_moeda_w			parametros_contas_pagar.cd_moeda_padrao%type;
nr_seq_baixa_w			titulo_pagar_baixa.nr_sequencia%type;
cd_tipo_baixa_enc_tit_w		pls_parametro_pagamento.cd_tipo_baixa_enc_tit%type;
nr_seq_lote_w			pls_pagamento_prestador.nr_seq_lote%type;
vl_titulo_w			titulo_pagar.vl_titulo%type;
cd_estabelecimento_w		pls_lote_pagamento.cd_estabelecimento%type;
nr_seq_trans_fin_baixa_rec_p_w	pls_parametro_pagamento.nr_seq_trans_fin_baixa_rec_pro%type;


BEGIN
if (nr_titulo_pagar_p IS NOT NULL AND nr_titulo_pagar_p::text <> '') then
	select	coalesce(max(cd_moeda_padrao),1)
	into STRICT	cd_moeda_w
	from	parametros_contas_pagar;

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_seq_baixa_w
	from	titulo_pagar_baixa
	where	nr_titulo = nr_titulo_pagar_p;

	select	max(nr_seq_lote),
		max(a.cd_estabelecimento)
	into STRICT	nr_seq_lote_w,
		cd_estabelecimento_w
	from	pls_pagamento_prestador	b,
		pls_lote_pagamento	a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	b.nr_sequencia		= nr_seq_pag_prestador_p;

	select	max(cd_tipo_baixa_enc_tit),
		max(nr_seq_trans_fin_baixa_rec_pro)
	into STRICT	cd_tipo_baixa_enc_tit_w,
		nr_seq_trans_fin_baixa_rec_p_w
	from	pls_parametro_pagamento
	where	cd_estabelecimento = cd_estabelecimento_w;

	if (coalesce(cd_tipo_baixa_enc_tit_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 267292, null ); -- É necessário cadastrar o parâmetro Tipo baixa pagar encontro. Cadastrar em OPS - Gestão de Operadoras -> Parâmetros da OPS -> Pagamento produção.
	end if;

	select	coalesce(max(vl_saldo_titulo), 0),
		max(nr_seq_trans_fin_baixa)
	into STRICT	vl_titulo_w,
		nr_seq_trans_fin_baixa_w
	from	titulo_pagar
	where	nr_titulo	= nr_titulo_pagar_p;

	if (nr_seq_trans_fin_baixa_rec_p_w IS NOT NULL AND nr_seq_trans_fin_baixa_rec_p_w::text <> '') then
		nr_seq_trans_fin_baixa_w := nr_seq_trans_fin_baixa_rec_p_w;
	end if;

	insert into titulo_pagar_baixa(nr_titulo,
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nr_seq_pag_item,
		cd_moeda,
		cd_tipo_baixa,
		ie_acao,
		dt_baixa,
		vl_baixa,
		vl_descontos,
		vl_devolucao,
		vl_juros,
		vl_multa,
		vl_pago,
		vl_inss,
		vl_ir,
		vl_glosa,
		vl_outras_deducoes,
		vl_outros_acrescimos,
		ds_observacao,
		nr_seq_trans_fin)
	values (nr_titulo_pagar_p,
		nr_seq_baixa_w,
		nm_usuario_p,
		clock_timestamp(),
		null,
		cd_moeda_w,
		cd_tipo_baixa_enc_tit_w,
		'I', /* Inclusão */
		clock_timestamp(),
		vl_titulo_w,
		0,
		0,
		0,
		0,
		vl_titulo_w,
		0,
		0,
		0,
		0,
		0,
		'Baixa gerada através do lote, ' || nr_seq_lote_w || ', de pagamento de produção médica.',
		nr_seq_trans_fin_baixa_w);

	CALL atualizar_saldo_tit_pagar( nr_titulo_pagar_p , nm_usuario_p );
	CALL gerar_w_tit_pag_imposto( nr_titulo_pagar_p , nm_usuario_p );

	-- É adicionado na obs "Título gerado a partir de um lote de pagamento para recurso próprio, liquidado sem pagamento"
	update	titulo_pagar
	set	ds_observacao_titulo	= CASE WHEN ds_observacao_titulo='' THEN  'Título gerado a partir de um lote de pagamento para recurso próprio, liquidado sem pagamento'  ELSE pls_util_pck.enter_w || 'Título gerado a partir de um lote de pagamento para recurso próprio, liquidado sem pagamento' END
	where	nr_titulo		= nr_titulo_pagar_p
	and	vl_saldo_titulo		= 0;
end if;

/* Não pode ter commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_baixar_tit_pag_rec_propr ( nr_titulo_pagar_p titulo_pagar.nr_titulo%type, nr_seq_pag_prestador_p pls_pagamento_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type ) FROM PUBLIC;

