CREATE OR REPLACE PROCEDURE pls_busca_prox_reg_sep ( ds_counteudo_p text, ds_separador_p text, ds_conteudo_novo_p INOUT text, ds_valor_p INOUT text) AS $body$
DECLARE


ds_conteudo_novo_w 	varchar(4000);
ds_valor_w		varchar(4000);


BEGIN
	if (position(ds_separador_p in ds_counteudo_p) > 0) then
		ds_valor_w := substr(ds_counteudo_p, 0, (position(ds_separador_p in ds_counteudo_p))-1);
