CREATE OR REPLACE PROCEDURE pls_cad_usuario_mobile (cd_usuario_plano_p text, ds_senha_p text, ds_hash_p text, ds_obervacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_segurado_web_p INOUT bigint, nr_seq_segurado_p INOUT bigint) AS $body$
DECLARE

 
nr_seq_segurado_web_w	pls_segurado_web.nr_sequencia%type;
nr_seq_segurado_w	pls_segurado_web.nr_seq_segurado%type;
qt_senha_w		bigint;			

BEGIN 
 
nr_seq_segurado_w := pls_obter_dados_carteira(null,cd_usuario_plano_p, 'S');
 
 
if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then 
 
	delete from pls_segurado_web 
	where nr_seq_segurado = nr_seq_segurado_w 
	and  ie_situacao = 'P';
	 
	select	count(1) 
	into STRICT	qt_senha_w 
	from	pls_segurado_web 
	where	nr_seq_segurado = nr_seq_segurado_w 
	and	coalesce(ds_senha::text, '') = '' 
	and	ie_situacao = 'A';
	 
	if (qt_senha_w > 0) then 
	 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_segurado_web_w 
		from	pls_segurado_web 
		where	nr_seq_segurado = nr_seq_segurado_w 
		and	ie_situacao = 'A';
	 
		update	pls_segurado_web 
		set	ds_senha = ds_senha_p,ie_situacao = 'A', 
			ds_hash = ds_hash_p, dt_atualizacao = clock_timestamp() , 
			nm_usuario = nm_usuario_p, ds_observacao = ds_obervacao_p, 
			cd_estabelecimento = cd_estabelecimento_p, ie_origem_login = 'AB' 
		where	nr_sequencia = nr_seq_segurado_web_w;		
		 
		 
	else 
	 
		select	nextval('pls_segurado_web_seq') 
		into STRICT	nr_seq_segurado_web_w 
		;
 
		insert	into	pls_segurado_web(nr_sequencia, nr_seq_segurado, ds_senha, 
						 ie_situacao, ds_hash, dt_atualizacao, 
						 nm_usuario,dt_atualizacao_nrec, nm_usuario_nrec, 
						 ds_observacao, cd_estabelecimento, ie_origem_login) 
					values (nr_seq_segurado_web_w, nr_seq_segurado_w, ds_senha_p, 
						 'A', ds_hash_p, clock_timestamp(), 
						 nm_usuario_p, clock_timestamp(), nm_usuario_p, 
						 ds_obervacao_p, cd_estabelecimento_p, 'AB');						
		 
	end if;
	nr_seq_segurado_web_p := nr_seq_segurado_web_w;
	nr_seq_segurado_p := nr_seq_segurado_w;
					 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
