SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_aut_faturada ( nr_seq_guia_plano_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Cancelar a guia faturada 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ X ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
------------------------------------------------------------------------------------------------------------------- 
Referências: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ie_status_w			varchar(2);
nr_seq_atend_pls_w		bigint;
nr_seq_evento_atend_w		bigint;
qt_registros_aud_w		bigint;

 
C07 CURSOR FOR 
	SELECT	a.nr_sequencia	nr_seq_conta 
	from	pls_conta   			a, 
		pls_conta_pos_estabelecido	b 
	where	a.nr_seq_guia = nr_seq_guia_plano_p 
	and	b.nr_seq_conta = a.nr_sequencia 
	and	a.ie_tipo_conta = 'C' 
	and	(b.nr_seq_lote_fat IS NOT NULL AND b.nr_seq_lote_fat::text <> '');

BEGIN 
 
	if (nr_seq_guia_plano_p IS NOT NULL AND nr_seq_guia_plano_p::text <> '') then 
 
		update	pls_guia_plano_proc 
		set	ie_status	= 'D' 
		where	nr_seq_guia	= nr_seq_guia_plano_p;
 
		update	pls_guia_plano_mat 
		set	ie_status	= 'D' 
		where	nr_seq_guia	= nr_seq_guia_plano_p;
 
		update	pls_guia_plano 
		set	dt_cancelamento		= clock_timestamp(), 
			ie_status		= 3, 
			ie_estagio		= 8, 
			nr_seq_motivo_cancel	= nr_seq_motivo_cancel_p, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_sequencia		= nr_seq_guia_plano_p;
 
		if (nr_seq_motivo_cancel_p IS NOT NULL AND nr_seq_motivo_cancel_p::text <> '') then 
 
			CALL pls_guia_gravar_historico(	nr_seq_guia_plano_p, 
							7, 
							substr('Cancelada pelo usuário '||nm_usuario_p||'.'||chr(13)||'Motivo: '|| 
							substr(pls_obter_desc_guia_mot_cancel(nr_seq_motivo_cancel_p),1,255),1,4000)||'.', 
							'AF', 
							nm_usuario_p);
			for r_C07_w in C07 loop 
 
				insert into pls_conta_log(nr_sequencia,	nm_usuario, dt_atualizacao, 
					nm_usuario_nrec, dt_atualizacao_nrec, nm_usuario_alteracao, 
					dt_alteracao, nr_seq_conta, nr_seq_conta_mat, 
					nr_seq_conta_proc, ds_alteracao) 
				values (nextval('pls_conta_log_seq'), nm_usuario_p, clock_timestamp(), 
					nm_usuario_p,clock_timestamp(),nm_usuario_p, 
					clock_timestamp(),r_C07_w.nr_seq_conta, null, 
					null, 'Guia faturada: '||nr_seq_guia_plano_p||' / Cancelada pelo usuário '||nm_usuario_p||'.');
			end loop;
 
		end if;
	end if;
	begin 
	select	nr_seq_atend_pls, 
		nr_seq_evento_atend, 
		ie_status 
	into STRICT	nr_seq_atend_pls_w, 
		nr_seq_evento_atend_w, 
		ie_status_w 
	from	pls_guia_plano 
	where	nr_sequencia	= nr_seq_guia_plano_p;
	exception 
	when others then 
		nr_seq_atend_pls_w	:= null;
	end;
 
	if (ie_status_w <> '2') and (nr_seq_atend_pls_w IS NOT NULL AND nr_seq_atend_pls_w::text <> '') then 
		CALL pls_finalizar_atendimento(	nr_seq_atend_pls_w, 
						nr_seq_evento_atend_w, 
						null, 
						null, 
						nm_usuario_p);
	end if;
 
	CALL pls_atualiza_estagio_guia_conv(	nr_seq_guia_plano_p, 
					nm_usuario_p);
 
	if (nr_seq_guia_plano_p IS NOT NULL AND nr_seq_guia_plano_p::text <> '') then 
		select	count(1) 
		into STRICT	qt_registros_aud_w 
		from	pls_auditoria 
		where	nr_seq_guia	= nr_seq_guia_plano_p 
		and	coalesce(dt_liberacao::text, '') = '';
 
		if (qt_registros_aud_w > 0) then 
			update	pls_auditoria 
			set	ie_status		= 'C', 
				dt_liberacao		= clock_timestamp(), 
				nr_seq_proc_interno	 = NULL, 
				dt_atualizacao		= clock_timestamp(), 
				nm_usuario		= nm_usuario_p 
			where	nr_seq_guia		= nr_seq_guia_plano_p;
		end if;
	end if;
 
	commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cancelar_aut_faturada ( nr_seq_guia_plano_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text) FROM PUBLIC;

