CREATE OR REPLACE PROCEDURE pls_cancelar_conta_ressarci ( nr_seq_conta_p pls_processo_conta.nr_sequencia%type, ds_motivo_cancelamento_p pls_processo_conta.ds_motivo_cancelamento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN


if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then

	update 	pls_processo_conta
	set	ie_status_conta 	= 'C',
		ds_motivo_cancelamento 	= ds_motivo_cancelamento_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_conta_p;

end if;

commit;

end;
