SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_cotacao ( nr_seq_cotacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Solicitar o cancelamento da cotação dos itens da análise
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_auditoria_w		pls_auditoria.nr_sequencia%type;

C01 CURSOR(nr_seq_cotacao_pc bigint) FOR
	SELECT	nr_sequencia,
		nr_seq_aud_item
	from	pls_item_cotacao
	where	nr_seq_cotacao	= nr_seq_cotacao_pc;


BEGIN

if (nr_seq_cotacao_p IS NOT NULL AND nr_seq_cotacao_p::text <> '') then

	for r_C01_w in C01(nr_seq_cotacao_p) loop
		begin

		-- Alterar o status do item para Finalizado
		update	pls_item_cotacao
		set	ie_status	= 4,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= r_C01_w.nr_sequencia
		and	ie_status 	<> 4;

		-- Alterar o status do item na auditoria para pendente.
		update	pls_auditoria_item
		set	ie_status	= 'P',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= r_C01_w.nr_seq_aud_item;

		insert into pls_item_cotacao_hist(
				nr_sequencia, dt_atualizacao, ie_tipo_historico,
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_item_cotacao, ds_historico)
		values (
				nextval('pls_item_cotacao_hist_seq'), clock_timestamp(), 1,
				nm_usuario_p, clock_timestamp(), nm_usuario_p,
				r_C01_w.nr_sequencia, 'O usuário '||nm_usuario_p||' cancelou a cotação.');
		end;
	end loop;

	-- Alterar o status da cotação para Cotacao cancelada
	update	pls_cotacao
	set	ie_status	= 4,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_cotacao_p;

	select	nr_seq_auditoria
	into STRICT	nr_seq_auditoria_w
	from	pls_cotacao
	where	nr_sequencia	= nr_seq_cotacao_p;

	-- Alterar o status da auditoria para Análise
	update	pls_auditoria
	set	ie_status	= 'A',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_auditoria_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cancelar_cotacao ( nr_seq_cotacao_p bigint, nm_usuario_p text) FROM PUBLIC;

