SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_defesa ( nr_seq_defesa_p bigint, nr_seq_motivo_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_conta_w			double precision;
nr_seq_conta_w			pls_impugnacao.nr_seq_conta%type;
nr_seq_impugnacao_w		pls_impugnacao_defesa.nr_seq_impugnacao%type;
qt_defesa_valida_w		integer;



BEGIN

select	nr_seq_impugnacao
into STRICT	nr_seq_impugnacao_w
from	pls_impugnacao_defesa
where	nr_sequencia	= nr_seq_defesa_p;

select	nr_seq_conta
into STRICT	nr_seq_conta_w
from	pls_impugnacao
where	nr_sequencia	= nr_seq_impugnacao_w;

update	pls_impugnacao_defesa
set	dt_cancelamento		= clock_timestamp(),
	nr_motivo_cancelamento	= nr_seq_motivo_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_defesa_p;


-- Tratativa do status da impugnação, e da conta quando ocorrer cancelamento.
-- Atualmente só será tratado caso todas as defesas sejam canceladas.
-- Não é o indicado colocar essa regra aqui, mas a tratativa de status não está centralizada
-- ou estruturada, e o ressarcimento vai entrar para o road map para refactoring,
--  portanto optei por não alterar as demais rotinas neste momento.
-- Primeiro avalia as defesas da impugnação
select	count(1)
into STRICT	qt_defesa_valida_w
from	pls_impugnacao_defesa
where	nr_seq_impugnacao	= nr_seq_impugnacao_w
and	coalesce(dt_cancelamento::text, '') = '';

if (coalesce(qt_defesa_valida_w,0) = 0) then
	update	pls_impugnacao
	set	ie_status_impugnacao	= 'A' -- Aguardando elaboração da defesa
	where	nr_sequencia	= nr_seq_impugnacao_w;
end if;

-- agora avalia as defesas da conta
select	count(1)
into STRICT	qt_defesa_valida_w
from	pls_impugnacao_defesa	a,
	pls_impugnacao		b
where	a.nr_seq_impugnacao	= b.nr_sequencia
and	b.nr_seq_conta		= nr_seq_conta_w
and	coalesce(dt_cancelamento::text, '') = '';

if (coalesce(qt_defesa_valida_w,0) = 0) then
	update	pls_processo_conta
	set	ie_instancia_conta	 = NULL
	where	nr_sequencia	= nr_seq_conta_w;
end if;

CALL pls_atualizar_valores_impug(nr_seq_impugnacao_w, null, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
