SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_fatura_geral ( nr_seq_fat_geral_p ptu_fatura_geral.nr_sequencia%type, ie_cancelar_titulo_p text, nm_usuario_p usuario.nm_usuario%type, nr_seq_motivo_canc_p ptu_mot_cancelamento_a580.nr_sequencia%type, ds_observacao_p ptu_fatura_geral.ds_observacao%type) AS $body$
DECLARE


nr_titulo_pagar_w	ptu_fatura_geral.nr_titulo_pagar%type;
nr_titulo_receber_w	ptu_fatura_geral.nr_titulo_receber%type;
qt_registro_w		integer;
nr_lote_contabil_w 	titulo_receber.nr_lote_contabil%type;



BEGIN

if (nr_seq_fat_geral_p IS NOT NULL AND nr_seq_fat_geral_p::text <> '') then


	-- levanda os dados da fatura
	select	nr_titulo_pagar,
		nr_titulo_receber
	into STRICT	nr_titulo_pagar_w,
		nr_titulo_receber_w
	from	ptu_fatura_geral
	where	nr_sequencia	= nr_seq_fat_geral_p  LIMIT 1;

	-- verifica se a fatura possui lote de faturamento gerado
	select 	count(1)
	into STRICT    qt_registro_w
	from    pls_fatura
	where   nr_titulo = nr_titulo_receber_w  LIMIT 1;

	if (qt_registro_w > 0) then
	       CALL wheb_mensagem_pck.exibir_mensagem_abort(1050677);
	end if;

	-- verifica se a fatura possui lote cont√°bil gerado
	select 	max(nr_lote_contabil)
	into STRICT    nr_lote_contabil_w
	from    titulo_receber
	where   nr_titulo = nr_titulo_receber_w  LIMIT 1;

	if (coalesce(nr_lote_contabil_w,'0')!= '0') then
	       CALL wheb_mensagem_pck.exibir_mensagem_abort(1050679);
	end if;

	-- cancela o a pagar
	if	(nr_titulo_pagar_w IS NOT NULL AND nr_titulo_pagar_w::text <> '' AND ie_cancelar_titulo_p = 'S') then
		CALL cancelar_titulo_pagar(nr_titulo_pagar_w, nm_usuario_p, clock_timestamp());
	end if;

	-- cancela o a receber
	if	(nr_titulo_receber_w IS NOT NULL AND nr_titulo_receber_w::text <> '' AND ie_cancelar_titulo_p = 'S') then
		CALL cancelar_titulo_receber(nr_titulo_receber_w, nm_usuario_p, 'S', clock_timestamp());
	end if;

	-- Atualiza os dados da fatura geral
	update	ptu_fatura_geral
	set	dt_cancelamento		= clock_timestamp(),
		nm_usuario_cancelamento	= nm_usuario_p,
		nr_seq_mot_cancelamento = nr_seq_motivo_canc_p,
		ds_observacao		= ds_observacao_p
	where	nr_sequencia		= nr_seq_fat_geral_p;

	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
