SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_guia_reenvio ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_reg_proc_w			bigint;
qt_reg_mat_w			bigint;

BEGIN

update	pls_guia_plano
set	ie_estagio	= 8,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_guia_p
and	ie_estagio	= 12;

select	count(1)
into STRICT	qt_reg_proc_w
from	pls_guia_plano_proc
where	nr_seq_guia	= nr_seq_guia_p;

if (qt_reg_proc_w	> 0) then
	update	pls_guia_plano_proc
	set	ie_status	= 'D',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_seq_guia	= nr_seq_guia_p
	and	ie_status	= 'I';
end if;

select	count(1)
into STRICT	qt_reg_mat_w
from	pls_guia_plano_mat
where	nr_seq_guia	= nr_seq_guia_p;

if (qt_reg_mat_w	> 0) then
	update	pls_guia_plano_mat
	set	ie_status	= 'D',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_seq_guia	= nr_seq_guia_p
	and	ie_status	= 'I';
end if;

CALL pls_guia_gravar_historico(	nr_seq_guia_p, 2, substr(
				'Guia cancelada através da função OPS - Manutenção de Processos Operacionais do Autorizador.'||chr(13)||
				'Usuário: '||nm_usuario_p||chr(13)||
				'Data: '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'),1,4000),
				'', nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
