CREATE OR REPLACE PROCEDURE pls_cancelar_guia_requisicao ( nr_seq_guia_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text) AS $body$
DECLARE

				
nr_seq_requisicao_w		bigint;
nr_seq_req_proc_w		bigint;
nr_seq_req_mat_w		bigint;
qt_item_w				pls_execucao_req_item.qt_item%type;

C01 CURSOR FOR
	SELECT	nr_seq_req_proc,
		nr_seq_req_mat,
		qt_item
	from	pls_execucao_req_item
	where	nr_seq_guia		= nr_seq_guia_p
	and	nr_seq_requisicao	= nr_seq_requisicao_w;


BEGIN

CALL pls_cancelar_autorizacao(nr_seq_guia_p, nr_seq_motivo_cancel_p, nm_usuario_p,null);

select	pls_obter_req_execucao_guia(nr_seq_guia_p)
into STRICT	nr_seq_requisicao_w
;

open C01;
loop
fetch C01 into	
	nr_seq_req_proc_w,
	nr_seq_req_mat_w,
	qt_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(nr_seq_req_proc_w,0) > 0) then
		update	pls_requisicao_proc
		set	qt_proc_executado = qt_proc_executado - qt_item_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia = nr_seq_req_proc_w;
	elsif (coalesce(nr_seq_req_mat_w,0) > 0) then
		update	pls_requisicao_mat
		set	qt_mat_executado = qt_mat_executado - qt_item_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia = nr_seq_req_mat_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
