SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_guia_web ( nr_seq_guia_p bigint, nr_seq_prestador_web_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_motivo_cancel_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE

 
nr_seq_prestador_web_w		bigint;
ds_retorno_w			varchar(255) := '';
dt_validade_senha_w		timestamp;
ie_estagio_w			smallint;
ie_status_w			varchar(2);
cd_guia_w			varchar(20);
nr_seq_segurado_w		bigint;
qt_registros_w			bigint;
ie_cancel_guia_local_atend_w		pls_web_param_geral.ie_cancel_guia_local_atend%type	:= 'N';
nr_seq_local_prest_req_w		pls_usuario_web.nr_seq_local_atend%type;
nr_seq_local_prest_portal_w		pls_usuario_web.nr_seq_local_atend%type;


BEGIN 
 
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then 
 
	begin 
		select	coalesce(nr_seq_prestador_web, 0), 
			ie_status, 
			ie_estagio, 
			cd_guia, 
			nr_seq_segurado 
		into STRICT	nr_seq_prestador_web_w, 
			ie_status_w, 
			ie_estagio_w, 
			cd_guia_w, 
			nr_seq_segurado_w 
		from	pls_guia_plano 
		where	nr_sequencia = nr_seq_guia_p;
	exception 
	when others then 
		nr_seq_prestador_web_w	:= 0;
	end;
	 
	begin 
		select	coalesce(ie_cancel_guia_local_atend,'N') 
		into STRICT	ie_cancel_guia_local_atend_w 
 
		from	pls_web_param_geral 
		where	cd_estabelecimento	= cd_estabelecimento_p;
	exception 
	when others then 
		ie_cancel_guia_local_atend_w	:= 'N';
	end;
 
	-- Obter o local de atendimento do prestador da requisição 
	select	nr_seq_local_atend 
	into STRICT	nr_seq_local_prest_req_w 
	from	pls_usuario_web 
	where	nr_sequencia	= nr_seq_prestador_web_w;
	 
	-- Obter o local de atendimento do prestador do portal 
	select	nr_seq_local_atend 
	into STRICT	nr_seq_local_prest_portal_w 
	from	pls_usuario_web 
	where	nr_sequencia	= nr_seq_prestador_web_p;
	 
	if (nr_seq_prestador_web_w	= nr_seq_prestador_web_p) or 
		((nr_seq_prestador_web_w <> nr_seq_prestador_web_p) and (ie_cancel_guia_local_atend_w	= 'S') and (nr_seq_local_prest_req_w	= nr_seq_local_prest_portal_w)) then 
	 
		select	count(1) 
		into STRICT	qt_registros_w 
		from	pls_conta 
		where	cd_guia		= cd_guia_w 
		and	nr_seq_segurado	= nr_seq_segurado_w;
		 
		if (qt_registros_w > 0) then 
			ds_retorno_w	:= 'Não é permitido cancelar uma guia já importada!';
		elsif (ie_estagio_w = 8) then 
			ds_retorno_w	:= 'Guia já cancelada!';
		elsif (ie_status_w <> '1') then 
			ds_retorno_w	:= 'Guia não autorizada!';
		else 
			/*Obter válidade da senha guia*/
 
			select	dt_validade_senha 
			into STRICT	dt_validade_senha_w 
			from	pls_guia_plano 
			where	nr_sequencia	= nr_seq_guia_p;
 
			if (dt_validade_senha_w	< clock_timestamp()) then 
				ds_retorno_w	:= 'Não é permitido cancelar uma guia com data de válidade vencida !';
			else 
				CALL pls_cancelar_guia_requisicao(nr_seq_guia_p, null, nm_usuario_p);
				update	pls_guia_plano 
				set	nr_seq_motivo_cancel = nr_seq_motivo_cancel_p 
				where	nr_sequencia = nr_seq_guia_p;
 
				if ((nr_seq_prestador_web_w <> nr_seq_prestador_web_p) and (ie_cancel_guia_local_atend_w	= 'S') and (nr_seq_local_prest_req_w	= nr_seq_local_prest_portal_w)) then 
				   
					CALL pls_guia_gravar_historico(nr_seq_guia_p, 7, 'Guia solicitada pelo usuário '||nr_seq_prestador_web_w 
								 ||' - '||substr(pls_obter_nm_usuario_web(nr_seq_prestador_web_w),1,255)||' foi cancelada pelo usuario '||nr_seq_prestador_web_p 
								 ||' - '||substr(pls_obter_nm_usuario_web(nr_seq_prestador_web_p),1,255)||', em '|| 
								 to_char(clock_timestamp(), 'dd/mm/yyyy hh24:mi:ss')||'.', '', nm_usuario_p);	
				else 
					CALL pls_guia_gravar_historico(nr_seq_guia_p, 7, 'Guia cancelada no portal web pelo usuário '||nr_seq_prestador_web_w 
								 ||' - '||substr(pls_obter_nm_usuario_web(nr_seq_prestador_web_w),1,255)||', em '|| 
								 to_char(clock_timestamp(), 'dd/mm/yyyy hh24:mi:ss')||'.', '', nm_usuario_p);	
				end if;
			end if;
		 
		end if;
	else 
		ds_retorno_w := 'Usuário logado diferente do usuário requisitante !';
	end if;
 
end if;
 
ds_retorno_p := ds_retorno_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cancelar_guia_web ( nr_seq_guia_p bigint, nr_seq_prestador_web_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_motivo_cancel_p bigint, ds_retorno_p INOUT text) FROM PUBLIC;

