SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_material_solic ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_material_p pls_material.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
- Excluir o material que havia sido inserido por uma Solicitação liberação mat/med.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
- Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_guia_plano_mat_w		pls_guia_plano_mat.nr_sequencia%type;
ie_tipo_anexo_w				pls_guia_plano_mat.ie_tipo_anexo%type;
qt_anexo_opme_w				integer;
qt_anexo_quimio_w			integer;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_guia_plano_mat_w
	from	pls_guia_plano_mat
	where	nr_seq_material	= nr_seq_material_p
	and		nr_seq_guia		= nr_seq_guia_p;

	select	ie_tipo_anexo
	into STRICT	ie_tipo_anexo_w
	from	pls_guia_plano_mat
	where	nr_sequencia	= nr_seq_guia_plano_mat_w;

	if (ie_tipo_anexo_w	= 'OP') then
		select	count(1)
		into STRICT	qt_anexo_opme_w
		from	pls_lote_anexo_mat_aut b
		where	b.nr_seq_lote_anexo_guia = (	SELECT	nr_sequencia
												from	pls_lote_anexo_guias_aut a
												where	nr_seq_guia	= nr_seq_guia_p
												and		ie_tipo_anexo	= 'OP');

		if (qt_anexo_opme_w = 1) then
			delete	from	pls_lote_anexo_guias_aut a
			where	nr_seq_guia	= nr_seq_guia_p
			and		ie_tipo_anexo	= 'OP';
		else
			delete	from pls_lote_anexo_mat_aut
			where	nr_seq_plano_mat	= nr_seq_guia_plano_mat_w;
		end if;

	elsif (ie_tipo_anexo_w	= 'QU') then
		select	count(1)
		into STRICT	qt_anexo_quimio_w
		from	pls_lote_anexo_mat_aut b
		where	b.nr_seq_lote_anexo_guia = (	SELECT	nr_sequencia
												from	pls_lote_anexo_guias_aut a
												where	nr_seq_guia	= nr_seq_guia_p
												and	ie_tipo_anexo	= 'QU');

		if (qt_anexo_quimio_w = 1) then
			delete	from	pls_lote_anexo_guias_aut a
			where	nr_seq_guia	= nr_seq_guia_p
			and		ie_tipo_anexo	= 'QU';
		else
			delete	from pls_lote_anexo_mat_aut
			where	nr_seq_plano_mat	= nr_seq_guia_plano_mat_w;
		end if;
	end if;

	delete	from pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_p
	and		nr_sequencia	= nr_seq_guia_plano_mat_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
