CREATE OR REPLACE PROCEDURE pls_cancelar_nf ( nr_sequencia_p bigint, nm_usuario_p text, ds_observacao_p text, ie_commit_p text) AS $body$
BEGIN
update	nota_fiscal 
set	ie_situacao 		= 9, 
	nr_seq_protocolo 	 = NULL, 
	nr_interno_conta	 = NULL, 
	nr_seq_exportada	 = NULL, 
	ie_status_envio	 = NULL, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp(), 
	dt_cancelamento		= clock_timestamp(), 
	nm_usuario_cancel	= nm_usuario_p 
where	nr_sequencia 		= nr_sequencia_p;
 
CALL Gerar_Titulo_Pagar_NF(nr_sequencia_p, nm_usuario_p);
 
CALL gerar_historico_nota_fiscal(nr_sequencia_p, nm_usuario_p, '1', ds_observacao_p);
 
if (ie_commit_p = 'S') then 
	commit;
end if;
 
end;
$body$
