SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_prospeccao ( nr_seq_cliente_p bigint, nr_seq_motivo_canc_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_tipo_atividade_w		bigint;
ds_status_w			varchar(255);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_tipo_atividade_w
from	pls_tipo_atividade
where	ie_status	= 'S';

select 	obter_valor_dominio(2664, ie_status)
into STRICT	ds_status_w
from	pls_comercial_cliente
where	nr_sequencia	= nr_seq_cliente_p;

update	pls_comercial_cliente
set	ie_status = 'N',
	nr_seq_motivo_cancelamento = nr_seq_motivo_canc_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_cliente_p;

insert into pls_comercial_historico(	nr_sequencia,
					nr_seq_cliente,
					cd_estabelecimento,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ds_titulo,
					ds_historico,
					dt_liberacao,
					nm_usuario_historico,
					ie_tipo_atividade,
					dt_historico)
				values (	nextval('pls_comercial_historico_seq'),
					nr_seq_cliente_p,
					cd_estabelecimento_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					'Alteração do status',
					'Alterado o status de "' || ds_status_w || '" para "Prospecção cancelada".',
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_tipo_atividade_w,
					clock_timestamp());

--pls_atualizar_status_cliente(nr_seq_cliente_p,nm_usuario_p);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
