CREATE OR REPLACE PROCEDURE pls_cancelar_protocolo ( nr_seq_protocolo_p bigint, ie_comitar_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_motivo_canc_p text default null) AS $body$
DECLARE

 
ie_motivo_canc_w	bigint;
ie_status_fatura_w	varchar(10);
ds_observacao_w		varchar(4000);

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_analise 
	from	pls_conta 
	where	nr_seq_protocolo = nr_seq_protocolo_p 
	order by 1;
	
BEGIN 
ie_motivo_canc_w := obter_valor_param_usuario(1293, 22, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p);
 
if (coalesce(ie_motivo_canc_w,0) = 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(221148);
end if;
 
select	max(ie_status) 
into STRICT	ie_status_fatura_w 
from	ptu_fatura 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
if (coalesce(ie_status_fatura_w::text, '') = '') then 
	select	max(fat.ie_status) 
	into STRICT	ie_status_fatura_w 
	from	ptu_fatura	fat 
	where	1 = 1 
	and	exists (	SELECT	1 
				from	pls_conta	conta 
				where	conta.nr_seq_fatura 	= fat.nr_sequencia 
				and	conta.nr_seq_protocolo 	= nr_seq_protocolo_p);
end if;
 
for r_c01_w in C01() loop 
	begin 
	if (ie_status_fatura_w in ('CA','R')) then 
		ds_observacao_w := pls_cancelar_conta(r_c01_w.nr_sequencia, null, nm_usuario_p, cd_estabelecimento_p, 1, ds_observacao_w, 'N');
	end if;
	 
	CALL pls_glosar_item_conta(r_c01_w.nr_sequencia, 0, 0, nm_usuario_p, cd_estabelecimento_p);
 
	if (coalesce(r_c01_w.nr_seq_analise,0) > 0) then		 
		CALL pls_alterar_status_analise_cta(r_c01_w.nr_seq_analise, 'C', 'PLS_CANCELAR_PROTOCOLO', nm_usuario_p, cd_estabelecimento_p);
	end if;
	 
	if (ie_status_fatura_w in ('CA','R')) then 
		ds_observacao_w := pls_cancelar_conta(r_c01_w.nr_sequencia, null, nm_usuario_p, cd_estabelecimento_p, 2, ds_observacao_w, 'N');
	end if;
	end;
end loop;
 
 
update	pls_protocolo_conta 
set	ie_status 		= 4, 
	nr_seq_motivo_cancel 	= ie_motivo_canc_w, 
	ds_motivo_canc		= ds_motivo_canc_p 
where	nr_sequencia 		= nr_seq_protocolo_p;
 
if (ie_comitar_p = 'S') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
