CREATE OR REPLACE PROCEDURE pls_cancelar_rec_faturamento (nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Cancelar as faturas do lote de cancelamento de receitas do faturamento. 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [X] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 	 
 
ie_cancelamento_w		varchar(1);
nr_seq_fatura_rec_w		bigint;
nr_seq_fatura_w			bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_seq_fatura 
	from	pls_cancel_rec_fatura a 
	where	a.nr_seq_lote	= nr_seq_lote_p;


BEGIN 
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_fatura_rec_w, 
		nr_seq_fatura_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		select	coalesce(a.ie_cancelamento, 'N') 
		into STRICT	ie_cancelamento_w 
		from	pls_fatura a 
		where	a.nr_sequencia = nr_seq_fatura_w;
		 
		if (ie_cancelamento_w <> 'C') then 
			CALL pls_cancelar_fatura(nr_seq_fatura_w, nr_seq_fatura_rec_w, nm_usuario_p, cd_estabelecimento_p, null, null);
			 
			update	pls_cancel_rec_fatura 
			set	ie_status 	= 'D', 
				nm_usuario	= nm_usuario_p, 
				dt_atualizacao	= clock_timestamp() 
			where	nr_sequencia	= nr_seq_fatura_rec_w;
		end if;
		end;
	end loop;
	close C01;
	 
	update	pls_cancel_rec_fat_lote 
	set	ie_status		= 'D', 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp(), 
		dt_cancelamento		= clock_timestamp() 
	where	nr_sequencia		= nr_seq_lote_p;
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
