SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_req_reenvio ( nr_seq_requisicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_reg_proc_w			bigint;
qt_reg_mat_w			bigint;

BEGIN

update	pls_requisicao
set	ie_estagio		= 3,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	dt_fim_processo_req	= clock_timestamp()
where	nr_sequencia	= nr_seq_requisicao_p
and	ie_estagio	= 10;

select	count(1)
into STRICT	qt_reg_proc_w
from	pls_requisicao_proc
where	nr_seq_requisicao	= nr_seq_requisicao_p;

if (qt_reg_proc_w	> 0) then
	update	pls_requisicao_proc
	set	ie_status		= 'C',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	ie_status		= 'I';
end if;

select	count(1)
into STRICT	qt_reg_mat_w
from	pls_requisicao_mat
where	nr_seq_requisicao	= nr_seq_requisicao_p;

if (qt_reg_mat_w	> 0) then
	update	pls_requisicao_mat
	set	ie_status		= 'C',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_seq_requisicao	= nr_seq_requisicao_p
	and	ie_status		= 'I';
end if;

CALL pls_requisicao_gravar_hist(	nr_seq_requisicao_p, 'L', substr(
				'Requisição cancelada através da função OPS - Manutenção de Processos Operacionais do Autorizador.'||chr(13)||
				'Usuário: '||nm_usuario_p||chr(13)||
				'Data: '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'),1,4000),
				null, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
