SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_rescisao_web ( nr_seq_solic_rescisao_p bigint, nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w			varchar(4000);

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Atualizar status da solicitação de requisição, deixando a mesma inativa.
Posteriormente, gerar  um histórico na tabela informando que o usuário  realizou o cancelamento da solicitação da rescisão.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (nr_seq_solic_rescisao_p IS NOT NULL AND nr_seq_solic_rescisao_p::text <> '') then
	if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then

	ds_historico_w := 'Ação: cancelamento solicitação de rescisão de contrato.';
	ds_historico_w := ds_historico_w||chr(10)||'Rescisão nr: '||nr_seq_solic_rescisao_p||' .';
	ds_historico_w := ds_historico_w||chr(10)||'Sequência do Segurado: '||nr_seq_segurado_p||' .';
	ds_historico_w := ds_historico_w||chr(10)||'Usuário Responsável: '||nm_usuario_p||' .';

		update	pls_rescisao_contrato
		set	ie_situacao ='I',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_solic_rescisao_p;

		CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '7', clock_timestamp(), ds_historico_w,
						null, null, null, null,
						null, null, null, null,
						null, null, null, null,
						nm_usuario_p, 'N');

			commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
