CREATE OR REPLACE PROCEDURE pls_cancelar_solic_matmed ( nr_seq_conta_p bigint, nr_seq_conta_mat_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_autorizacao_especial_w	bigint;
nr_seq_restricao_w		bigint;
nr_seq_material_w		bigint;
dt_atendimento_w		timestamp;
cd_guia_ref_w			varchar(25);
nr_seq_segurado_w		bigint;
nr_seq_guia_w			bigint;
nr_seq_solic_lib_mat_med_w	bigint;


BEGIN 
 
begin 
select	a.nr_seq_material, 
	a.dt_atendimento, 
	b.nr_seq_segurado, 
	coalesce(b.cd_guia_referencia,b.cd_guia) 
into STRICT	nr_seq_material_w, 
	dt_atendimento_w, 
	nr_seq_segurado_w, 
	cd_guia_ref_w 
from	pls_conta_mat a, 
	pls_conta b 
where	a.nr_seq_conta	= b.nr_sequencia 
and	a.nr_sequencia	= nr_seq_conta_mat_p;
exception 
when others then 
	nr_seq_material_w 	:= null;
	dt_atendimento_w 	:= null;
	cd_guia_ref_w		:= null;
end;
 
select	max(nr_sequencia) 
into STRICT	nr_seq_guia_w 
from	pls_guia_plano 
where	cd_guia 	= cd_guia_ref_w 
and	nr_seq_segurado = nr_seq_segurado_w;
 
select	max(nr_sequencia) 
into STRICT	nr_seq_solic_lib_mat_med_w 
from	pls_solic_lib_mat_med 
where	nr_seq_segurado = nr_seq_segurado_w 
and	nr_seq_material = nr_seq_material_w 
and	nr_seq_guia	= nr_seq_guia_w 
and	ie_status in (1,2);
 
if (coalesce(nr_seq_solic_lib_mat_med_w,0) > 0) then 
	CALL pls_atualizar_mat_med_solic(nr_seq_solic_lib_mat_med_w, 'R', nm_usuario_p,'N');
end if;
 
--commit; 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
