SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_titulos_pag_inad ( nr_seq_pagador_p bigint, ds_motivo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

						 
nr_titulo_w			bigint;
vl_saldo_titulo_w		double precision;
ie_liquidar_titulos_rescisao_w	varchar(1);
cd_tipo_receb_rescisao_w	integer;
nr_seq_trans_fin_rescisao_w	bigint;
ie_gerar_cobr_rescisao_w	varchar(1);
qt_titulo_cobranca_w		bigint;
nr_seq_cobranca_w		bigint;

C01 CURSOR FOR 
SELECT	a.nr_titulo, 
	a.vl_saldo_titulo 
from	pls_contrato_pagador	c, 
	pls_mensalidade		b, 
	titulo_receber		a 
where	a.nr_seq_mensalidade	= b.nr_sequencia 
and	b.nr_seq_pagador	= c.nr_sequencia 
and	c.nr_sequencia		= nr_seq_pagador_p 
and	coalesce(a.dt_liquidacao::text, '') = '';						
 

BEGIN 
 
if (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') and (ds_motivo_p IS NOT NULL AND ds_motivo_p::text <> '') then 
	select	max(cd_tipo_receb_rescisao), 
		max(nr_seq_trans_fin_rescisao), 
		max(ie_gerar_cobr_rescisao) 
	into STRICT	cd_tipo_receb_rescisao_w, 
		nr_seq_trans_fin_rescisao_w, 
		ie_gerar_cobr_rescisao_w 
	from	pls_parametros_cr a 
	where	a.cd_estabelecimento	= cd_estabelecimento_p;
	 
 
	if (coalesce(cd_tipo_receb_rescisao_w::text, '') = '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(185689);
		--'Não foi informado o tipo de recebimento para liquidação dos títulos na rescisão. Verifique em OPS - Gestão de Operadoras -> Parâmetros OPS -> Contas a Receber. 
	end if;
	 
	if (coalesce(nr_seq_trans_fin_rescisao_w::text, '') = '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(185690);
		--Não foi informada a transação financeira para liquidação dos títulos na rescisão. Verifique em OPS - Gestão de Operadoras -> Parâmetros OPS -> Contas a Receber. 
	end if;
 
	open C01;
	loop 
	fetch C01 into	 
		nr_titulo_w, 
		vl_saldo_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		if (coalesce(ie_gerar_cobr_rescisao_w,'N') = 'S') then 
			select	count(*) 
			into STRICT	qt_titulo_cobranca_w 
			from	cobranca 
			where	nr_titulo	= nr_titulo_w;
			 
			if (qt_titulo_cobranca_w = 0) then 
				CALL importar_cobranca(nr_titulo_w, 'N', nm_usuario_p, null);
				 
				select	max(nr_sequencia) 
				into STRICT	nr_seq_cobranca_w 
				from	cobranca a 
				where	nr_titulo	= nr_titulo_w;
				 
				CALL tratar_destino_cobranca(nr_seq_cobranca_w,null,cd_estabelecimento_p,nm_usuario_p);
			end if;
		end if;
		 
		CALL baixa_titulo_receber(cd_estabelecimento_p, 
			cd_tipo_receb_rescisao_w, 
			nr_titulo_w, 
			nr_seq_trans_fin_rescisao_w, 
			vl_saldo_titulo_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			null, 
			null, 
			null, 
			0, 
			0);
			 
		CALL atualizar_saldo_tit_rec(nr_titulo_w,nm_usuario_p);
		 
		insert into titulo_receber_hist(nr_sequencia, dt_atualizacao, nm_usuario, 
						nm_usuario_nrec, dt_atualizacao_nrec, nr_titulo, 
						ds_historico, dt_historico) 
					values (nextval('titulo_receber_hist_seq'), clock_timestamp(), nm_usuario_p, 
						nm_usuario_p, clock_timestamp(), nr_titulo_w, 
						'Cancelamento dos títulos do contrato - '||ds_motivo_p, clock_timestamp());
		end;
	end loop;
	close C01;
	insert into pls_pagador_historico(	nr_sequencia, dt_atualizacao, nm_usuario, 
							nm_usuario_nrec, dt_atualizacao_nrec, nr_seq_pagador, 
							cd_estabelecimento, ds_titulo, ds_historico, 
							dt_historico, nm_usuario_historico, ie_origem) 
				values (	nextval('pls_pagador_historico_seq'), clock_timestamp(), nm_usuario_p, 
						nm_usuario_p, clock_timestamp(), nr_seq_pagador_p, 
						cd_estabelecimento_p, 'Cancelamento dos títulos do contrato',ds_motivo_p, 
						clock_timestamp(), nm_usuario_p,'FF');
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cancelar_titulos_pag_inad ( nr_seq_pagador_p bigint, ds_motivo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

