CREATE OR REPLACE PROCEDURE pls_cancelar_tit_desdob_reemb ( nr_titulo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w		bigint;	
 

BEGIN 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	select	max(a.nr_titulo) 
	into STRICT	nr_titulo_w 
	from	titulo_pagar	a 
	where	a.nr_titulo_original	= nr_titulo_p;
 
	CALL pls_cancelar_tit_desdob_reemb(nr_titulo_w, cd_estabelecimento_p, nm_usuario_p);
 
	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then 
		CALL cancelar_titulo_pagar(nr_titulo_w, nm_usuario_p, clock_timestamp());
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
