SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_tit_lote_ret_trib ( nr_seq_lote_ret_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w		bigint;
nr_sequencia_w		pls_lote_pagamento.nr_sequencia%type;
nr_seq_lote_ret_trib_w 	pls_lote_ret_trib_valor.nr_sequencia%type;

C01 CURSOR FOR 
	SELECT	c.nr_titulo, 
		c.nr_sequencia 
	from	pls_lote_ret_trib_valor c, 
		pls_lote_ret_trib_prest	b, 
		pls_lote_retencao_trib	a 
	where	b.nr_sequencia = c.nr_seq_trib_prest 
	and	a.nr_sequencia = b.nr_seq_lote 
	and	a.nr_sequencia = nr_seq_lote_ret_p 
	and	(c.nr_titulo IS NOT NULL AND c.nr_titulo::text <> '');


BEGIN 
if (nr_seq_lote_ret_p IS NOT NULL AND nr_seq_lote_ret_p::text <> '') then 
	 
	select	max(nr_sequencia) 
	into STRICT	nr_sequencia_w 
	from	pls_lote_pagamento 
	where	nr_seq_lote_ret_trib	= nr_seq_lote_ret_p;
	 
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
		-- Não é possível cancelar e desvincular títulos de retenção deste mês de competência pois o lote de pagamento #@NR_SEQUENCIA#@ está vinculado a este lote de retenção. 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(354121, 'NR_SEQUENCIA=' || nr_sequencia_w);
	end if;
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_titulo_w, 
		nr_seq_lote_ret_trib_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL cancelar_titulo_pagar(nr_titulo_w, nm_usuario_p, clock_timestamp());
		 
		update	pls_pag_prest_venc_trib 
		set	nr_titulo_pagar_ret  = NULL 
		where 	nr_seq_lote_ret_trib_valor = nr_seq_lote_ret_trib_w;
		end;
	end loop;
	close C01;
	 
	update	pls_lote_ret_trib_valor 
	set	nr_titulo 	 = NULL, 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp() 
	where	nr_seq_trib_prest in (	SELECT	x.nr_sequencia 
					from	pls_lote_ret_trib_prest	x, 
						pls_lote_retencao_trib	y 
					where	y.nr_sequencia = x.nr_seq_lote 
					and	y.nr_sequencia = nr_seq_lote_ret_p);
	 
	update	pls_lote_retencao_trib 
	set	dt_geracao_titulos 	 = NULL, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_sequencia		= nr_seq_lote_ret_p;
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
