SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancelar_tit_pgto_prest ( nr_seq_pag_prestador_p bigint, ie_desvinc_p text, dt_cancelamento_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w			bigint;						
nr_seq_pag_prestador_w		bigint;					
nr_seq_prestador_w		bigint;
nr_seq_lote_w			bigint;
nr_seq_vencimento_w		bigint;
nr_seq_escrit_w			titulo_pagar_escrit.nr_seq_escrit%type;
nr_bordero_w			bordero_tit_pagar.nr_bordero%type;

C01 CURSOR FOR 
	SELECT	c.nr_titulo, 
		b.nr_sequencia, 
		b.nr_seq_prestador, 
		b.nr_seq_lote 
	from	titulo_pagar		c, 
		pls_pagamento_prestador	b 
	where	b.nr_sequencia		= nr_seq_pag_prestador_p 
	and	c.nr_seq_pls_pag_prest	= b.nr_sequencia;
	
C02 CURSOR FOR 
	SELECT	c.nr_titulo, 
		b.nr_sequencia, 
		b.nr_seq_prestador, 
		b.nr_seq_lote 
	from	titulo_receber			c, 
		pls_pag_prest_vencimento	a, 
		pls_pagamento_prestador		b 
	where	c.nr_titulo		= a.nr_titulo_receber 
	and	a.nr_seq_pag_prestador	= b.nr_sequencia 
	and	b.nr_sequencia		= nr_seq_pag_prestador_p;
	
C03 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_pag_prest_vencimento	a 
	where	a.nr_seq_pag_prestador	= nr_seq_pag_prestador_p;


BEGIN 
 
if (nr_seq_pag_prestador_p IS NOT NULL AND nr_seq_pag_prestador_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_titulo_w, 
		nr_seq_pag_prestador_w, 
		nr_seq_prestador_w, 
		nr_seq_lote_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
 
		select	max(nr_bordero) 
		into STRICT	nr_bordero_w 
		from	titulo_pagar_bordero_v 
		where	nr_titulo	= nr_titulo_w;
			 
		if (nr_bordero_w IS NOT NULL AND nr_bordero_w::text <> '') then 
				CALL wheb_mensagem_pck.exibir_mensagem_abort(283640, 'NR_TITULO=' || nr_titulo_w || ';NR_BORDERO=' || nr_bordero_w);
				-- O título #@NR_TITULO#@ já está no borderô de pagamento #@NR_BORDERO#@! 
				-- Não é possível cancelar este pagamento. 
		end if;
 
		select	max(nr_seq_escrit) 
		into STRICT	nr_seq_escrit_w 
		from	titulo_pagar_escrit 
		where	nr_titulo	= nr_titulo_w;
 
		if (nr_seq_escrit_w IS NOT NULL AND nr_seq_escrit_w::text <> '') then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(283646, 'NR_TITULO=' || nr_titulo_w || ';NR_SEQ_ESCRIT=' || nr_bordero_w);
			-- O título #@NR_TITULO#@ já está no pagamento_escritural #@NR_SEQ_ESCRIT#@! 
				-- Não é possível cancelar este pagamento. 
		end if;
 
		CALL cancelar_titulo_pagar(nr_titulo_w, nm_usuario_p, coalesce(dt_cancelamento_p, clock_timestamp()));
		 
		if (coalesce(ie_desvinc_p,'S') = 'S') then 
			update 	pls_pag_prest_vencimento 
			set	nr_titulo 		 = NULL 
			where	nr_seq_pag_prestador 	= nr_seq_pag_prestador_w 
			and	nr_titulo		= nr_titulo_w;
		end if;
		 
		update	titulo_pagar 
		set	nr_seq_pls_pag_prest  = NULL, 
			ds_observacao_titulo = 	('Este título havia sido gerado pelo lote de pagamento ' || 
						nr_seq_lote_w || ' e o prestador ' || nr_seq_prestador_w || '.')	 
		where	nr_titulo = nr_titulo_w;
		end;
	end loop;
	close C01;
	 
	open C02;
	loop 
	fetch C02 into	 
		nr_titulo_w, 
		nr_seq_pag_prestador_w, 
		nr_seq_prestador_w, 
		nr_seq_lote_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		CALL cancelar_titulo_receber(nr_titulo_w, nm_usuario_p, 'N',clock_timestamp());
		 
		if (coalesce(ie_desvinc_p,'S') = 'S') then 
			update 	pls_pag_prest_vencimento 
			set	nr_titulo_receber	 = NULL 
			where	nr_seq_pag_prestador 	= nr_seq_pag_prestador_w 
			and	nr_titulo_receber	= nr_titulo_w;
		end if;
		 
		update	titulo_receber 
		set	ds_observacao_titulo = 	('Este título havia sido gerado pelo lote de pagamento ' || 
						nr_seq_lote_w || ' e o prestador ' || nr_seq_prestador_w || '.')	 
		where	nr_titulo = nr_titulo_w;
		end;
	end loop;
	close C02;
	 
	open C03;
	loop 
	fetch C03 into	 
		nr_seq_vencimento_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin 
		update 	pls_pag_prest_vencimento 
		set	ie_proximo_pgto	= 'N' 
		where	nr_sequencia	= nr_seq_vencimento_w;
		end;
	end loop;
	close C03;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_cancelar_tit_pgto_prest ( nr_seq_pag_prestador_p bigint, ie_desvinc_p text, dt_cancelamento_p timestamp, nm_usuario_p text) FROM PUBLIC;

