CREATE OR REPLACE PROCEDURE pls_cancelar_tit_quota_part ( nr_seq_escrituracao_p pls_escrit_quota_parcela.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

 
C01 CURSOR(nr_seq_escrituracao_cp 	pls_escrit_quota_parcela.nr_seq_escrituracao%type) FOR 
SELECT	nr_titulo, 
	nr_sequencia 
from	pls_escrit_quota_parcela 
where	nr_seq_escrituracao = nr_seq_escrituracao_cp;

BEGIN 
 
for	r_c01_w in c01(nr_seq_escrituracao_p) loop 
 
	CALL cancelar_titulo_receber(r_c01_w.nr_titulo,nm_usuario_p,'N',clock_timestamp());
	 
	update	pls_escrit_quota_parcela 
	set	nr_titulo		 = NULL 
	where	nr_sequencia		= r_c01_w.nr_sequencia;
		 
end loop;
 
update	pls_escrituracao_quota 
set	dt_geracao_titulos	 = NULL, 
	dt_atualizacao		= clock_timestamp(), 
	nm_usuario		= nm_usuario_p 
where	nr_sequencia		= nr_seq_escrituracao_p;
 
commit;
 
end;
$body$
