CREATE OR REPLACE PROCEDURE pls_cancelar_tratamento_benef ( nr_seq_requisicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_trat_benef_w		bigint;
qt_reg_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	into STRICT	nr_seq_trat_benef_w
	from	pls_tratamento_benef
	where	nr_seq_requisicao	= nr_seq_requisicao_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_trat_benef_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_reg_w
	from	pls_requisicao_proc
	where	nr_seq_tratamento = nr_seq_trat_benef_w
	and	ie_status in ('P','S')
	and	nr_seq_requisicao <> nr_seq_requisicao_p;

	if (qt_reg_w = 0) then

		update	pls_tratamento_benef
		set	ie_estagio_tratamento	= 3,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_trat_benef_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
