SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_cancela_solic_mat_med ( nr_seq_solicitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Cancelar a solicitação de liberação de material / medicamento
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/qt_autorizado_w		bigint;
nr_seq_guia_w		bigint;
nr_seq_material_w	bigint;


BEGIN
	if ((nr_seq_solicitacao_p IS NOT NULL AND nr_seq_solicitacao_p::text <> '') and nr_seq_solicitacao_p <> 0) then
		select	nr_seq_guia,
			nr_seq_material
		into STRICT	nr_seq_guia_w,
			nr_seq_material_w
		from 	pls_solic_lib_mat_med
		where	nr_sequencia = nr_seq_solicitacao_p;

		update	pls_solic_lib_mat_med
		set	ie_status = 5
		where	nr_sequencia = nr_seq_solicitacao_p;

		insert	into pls_solic_lib_historico(nr_sequencia, nr_seq_solic_lib, dt_atualizacao,
			 nm_usuario, ds_observacao, dt_historico,
			 ie_tipo_historico, nr_seq_historico)
		values (nextval('pls_solic_lib_historico_seq'), nr_seq_solicitacao_p, clock_timestamp(),
			 nm_usuario_p, 'Cancelado', clock_timestamp(),
			 4, null);

		if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
			select	count(*)
			into STRICT	qt_autorizado_w
			from	pls_guia_plano_mat
			where	nr_seq_material	= nr_seq_material_w
			and	nr_sequencia	= nr_seq_guia_w;

			if (qt_autorizado_w > 0) then
				update	pls_guia_plano_mat
				set 	ie_status  = 'D'
				where	nr_seq_material	= nr_seq_material_w
				and	nr_seq_guia	= nr_seq_guia_w;
			end if;
		end if;
	end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
