CREATE OR REPLACE PROCEDURE pls_carencia_beforepost ( nr_seq_tipo_carencia_p bigint, nr_seq_grupo_carencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_retorno_sca_w	varchar(2);
ie_cpt_w		varchar(1);


BEGIN

select	max(substr(pls_verificar_utilizacao_sca(nr_seq_tipo_carencia_p,3),1,3))
into STRICT	ds_retorno_sca_w
;

select	max(ie_cpt)
into STRICT	ie_cpt_w
from	pls_tipo_carencia
where	nr_sequencia = nr_seq_tipo_carencia_p;

if (ie_cpt_w = 'S') then
	-- Não é possível cadastrar uma Cobertura parcial temporária. Favor verifique!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(120942);
end if;

if (ds_retorno_sca_w = '') and (coalesce(nr_seq_tipo_carencia_p,0) > 0) then
	-- A carência #@nr_seq_carencia#@ não é utilizada para Contratos/Produtos. Favor verifique!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(120944, 'NR_SEQ_CARENCIA='||nr_seq_tipo_carencia_p);
end if;

if (coalesce(nr_seq_tipo_carencia_p,0) > 0) and (coalesce(nr_seq_grupo_carencia_p,0) > 0) then
	-- Deve ser selecionado a Carência ou o Grupo de carência. Favor verifique!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(120948);
end if;

end;
$body$
