SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_carga_beneficiario ( cd_carteira_benef_p text, cd_contrato_ant_p text, cd_carteira_titular_p text, cd_pagador_p text, cd_plano_ant_p text, cd_tabela_preco_ant_p text, dt_contratacao_p timestamp, nr_seq_parentesco_p bigint, dt_rescisao_p timestamp, dt_inclusao_p timestamp, cd_codigo_anterior_p text, ie_tipo_segurado_p text, nr_cco_p bigint, dt_validade_cart_p timestamp, nr_via_cart_p bigint, ie_digito_cco_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
nr_seq_contrato_w		bigint;
cd_pessoa_titular_w		varchar(10);
ie_tipo_pagador_w		varchar(2);
nr_seq_pagador_w		bigint;
nr_seq_plano_w			bigint;
nr_seq_tabela_preco_w		bigint;
cd_pessoa_pagador_w		varchar(10);
ie_encontrou_pagador_w		varchar(10) := 'N';

C01 CURSOR FOR
	SELECT	cd_pessoa_fisica
	from	pessoa_fisica
	where	nr_cpf	= cd_pagador_p;


BEGIN

/* Obter a pessoa física da carteirinha */

select	coalesce(max(cd_pessoa_fisica),'')
into STRICT	cd_pessoa_fisica_w
from	pls_pessoa_carteira
where	cd_usuario_plano	= cd_carteira_benef_p;

/* Obter o contrato através do código anterior */

select	max(nr_sequencia)
into STRICT	nr_seq_contrato_w
from	pls_contrato
where	cd_cod_anterior	= cd_contrato_ant_p;

/* Obter a pessoa física do titular */

select	coalesce(max(cd_pessoa_fisica),'')
into STRICT	cd_pessoa_titular_w
from	pls_pessoa_carteira
where	cd_usuario_plano	= cd_carteira_titular_p;

/* Obter o pagador */

if (length(cd_pagador_p)	= 14) then
	ie_tipo_pagador_w	:= 'PJ';
else
	ie_tipo_pagador_w	:= 'PF';
end if;
if (ie_tipo_pagador_w	= 'PJ') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_pagador_w
	from	pls_contrato_pagador
	where	nr_seq_contrato		= nr_seq_contrato_w
	and	cd_cgc			= cd_pagador_p;
elsif (ie_tipo_pagador_w	= 'PF') then
	--aaschlote 14/04/20101 OS 306320 - Buscar o pagador até encontrar um no contrato vinculado
	open C01;
	loop
	fetch C01 into
		cd_pessoa_pagador_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (ie_encontrou_pagador_w	= 'N') then
			select	max(nr_sequencia)
			into STRICT	nr_seq_pagador_w
			from	pls_contrato_pagador
			where	nr_seq_contrato		= nr_seq_contrato_w
			and	cd_pessoa_fisica	= cd_pessoa_pagador_w;

			if (coalesce(nr_seq_pagador_w::text, '') = '') then
				ie_encontrou_pagador_w	:= 'N';
			else
				ie_encontrou_pagador_w	:= 'S';
			end if;
		end if;

		end;
	end loop;
	close C01;
end if;

/* Obter o produto */

select	max(nr_sequencia)
into STRICT	nr_seq_plano_w
from	pls_plano
where	cd_codigo_ant	= cd_plano_ant_p;

/* Obter a tabela de preço */

select	max(nr_sequencia)
into STRICT	nr_seq_tabela_preco_w
from	pls_tabela_preco
where	cd_codigo_ant	= cd_tabela_preco_ant_p;

insert into w_pls_carga_segurado(nr_sequencia, cd_estabelecimento, cd_pessoa_fisica,
	dt_atualizacao, nm_usuario, cd_usuario_plano,
	cd_pessoa_titular, nr_seq_contrato, nr_seq_pagador,
	nr_seq_plano, nr_seq_tabela, dt_contratacao,
	nr_seq_parentesco, dt_rescisao, dt_inclusao_operadora,
	cd_cod_anterior, ie_tipo_segurado, ie_status,nr_cco,ie_digito_cco,
	dt_validade_carteira,nr_via_carteira)
values (	nextval('w_pls_carga_segurado_seq'), cd_estabelecimento_p, cd_pessoa_fisica_w,
	clock_timestamp(), nm_usuario_p, cd_carteira_benef_p,
	cd_pessoa_titular_w, nr_seq_contrato_w, nr_seq_pagador_w,
	nr_seq_plano_w, nr_seq_tabela_preco_w, dt_contratacao_p,
	nr_seq_parentesco_p, dt_rescisao_p, dt_inclusao_p,
	cd_codigo_anterior_p, ie_tipo_segurado_p, 'L',nr_cco_p,ie_digito_cco_p,
	dt_validade_cart_p,nr_via_cart_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_carga_beneficiario ( cd_carteira_benef_p text, cd_contrato_ant_p text, cd_carteira_titular_p text, cd_pagador_p text, cd_plano_ant_p text, cd_tabela_preco_ant_p text, dt_contratacao_p timestamp, nr_seq_parentesco_p bigint, dt_rescisao_p timestamp, dt_inclusao_p timestamp, cd_codigo_anterior_p text, ie_tipo_segurado_p text, nr_cco_p bigint, dt_validade_cart_p timestamp, nr_via_cart_p bigint, ie_digito_cco_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

