SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_carregar_caren_abran_ant ( nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_plano_atual_w		bigint;
nr_seq_segurado_ant_w		bigint;
ie_abrangencia_ant_w		varchar(10);
ie_abrangencia_atual_w		varchar(10);
dt_contratacao_w		timestamp;
nr_seq_tipo_carencia_w		bigint;
qt_dias_w			bigint;
nr_seq_grupo_carencia_w		bigint;
ds_abrangencia_ant_w		varchar(255);

C01 CURSOR FOR
	SELECT	nr_seq_tipo_carencia,
		nr_seq_grupo_carencia,
		coalesce(qt_dias_fora_abrang_ant,qt_dias)
	from	pls_carencia
	where	nr_seq_segurado	= nr_seq_segurado_p
	and	ie_cpt		= 'N';


BEGIN

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	begin
	select	a.ie_abrangencia,
		a.nr_sequencia,
		b.nr_seq_segurado_ant,
		b.dt_contratacao
	into STRICT	ie_abrangencia_atual_w,
		nr_seq_plano_atual_w,
		nr_seq_segurado_ant_w,
		dt_contratacao_w
	from	pls_segurado	b,
		pls_plano	a
	where	b.nr_seq_plano	= a.nr_sequencia
	and	b.nr_sequencia	= nr_seq_segurado_p;
	exception
	when no_data_found then
		nr_seq_segurado_ant_w := null;
	end;
	
	delete	FROM w_pls_carencia_abrang_ant
	where	nr_seq_segurado	= nr_seq_segurado_p
	and	nm_usuario = nm_usuario_p;
	
	select	max(b.ie_abrangencia),
		coalesce(max(a.dt_alteracao), dt_contratacao_w)
	into STRICT	ie_abrangencia_ant_w,
		dt_contratacao_w
	from	pls_segurado_alt_plano a,
		pls_plano b
	where	b.nr_sequencia		= a.nr_seq_plano_ant
	and	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.nr_seq_plano_atual	= nr_seq_plano_atual_w
	and	a.ie_situacao		= 'A';
	
	if	((coalesce(ie_abrangencia_ant_w::text, '') = '') and (nr_seq_segurado_ant_w IS NOT NULL AND nr_seq_segurado_ant_w::text <> '')) then
		select	max(a.ie_abrangencia)
		into STRICT	ie_abrangencia_ant_w
		from	pls_segurado	b,
			pls_plano	a
		where	b.nr_seq_plano	= a.nr_sequencia
		and	b.nr_sequencia	= nr_seq_segurado_ant_w;
	end if;
	
	if (ie_abrangencia_ant_w IS NOT NULL AND ie_abrangencia_ant_w::text <> '') then
		if	((ie_abrangencia_ant_w = 'E') and (ie_abrangencia_atual_w = 'N') or
			((ie_abrangencia_ant_w = 'GM') and (ie_abrangencia_atual_w = 'E') or
			((ie_abrangencia_ant_w = 'GM') and (ie_abrangencia_atual_w = 'N') or
			((ie_abrangencia_ant_w = 'M') and (ie_abrangencia_atual_w = 'E') or
			(ie_abrangencia_ant_w = 'M' AND ie_abrangencia_atual_w = 'N'))))) then
			
			if (ie_abrangencia_atual_w = 'GM') then
				ds_abrangencia_ant_w	:= wheb_mensagem_pck.get_texto(1108792);
			elsif (ie_abrangencia_atual_w = 'M') then
				ds_abrangencia_ant_w	:= wheb_mensagem_pck.get_texto(1108793);
			elsif (ie_abrangencia_atual_w = 'E') then
				ds_abrangencia_ant_w	:= wheb_mensagem_pck.get_texto(1108794);
			elsif (ie_abrangencia_atual_w = 'N') then
				ds_abrangencia_ant_w	:= wheb_mensagem_pck.get_texto(1108795);
			end if;
			
			open C01;
			loop
			fetch C01 into
				nr_seq_tipo_carencia_w,
				nr_seq_grupo_carencia_w,
				qt_dias_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				insert into w_pls_carencia_abrang_ant(	nm_usuario,nr_seq_tipo_carencia,nr_seq_grupo_carencia,dt_inicio_vigencia,
						nr_seq_segurado,dt_fim_vigencia,qt_dias,ds_abrangencia_ant)
				values (	nm_usuario_p,nr_seq_tipo_carencia_w,nr_seq_grupo_carencia_w,dt_contratacao_w,
						nr_seq_segurado_p,dt_contratacao_w+qt_dias_w,qt_dias_w,ds_abrangencia_ant_w);
				end;
			end loop;
			close C01;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_carregar_caren_abran_ant ( nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

