	dt_atualizacao_nrec, nm_usuario_nrec, ie_tipo_consulta,
	nr_seq_item_exec, nr_seq_execucao, nr_seq_segurado,
	nr_seq_prestador)
values (nr_seq_tab_temp_exec_w, clock_timestamp(), nm_usuario_p,
	clock_timestamp(), nm_usuario_p, 'LE',
	nr_seq_exec_item_p, nr_seq_execucao_p, nr_seq_segurado_p,
	nr_seq_prestador_w);


end;
