CREATE OR REPLACE PROCEDURE pls_carregar_itens_simul_fator ( nr_seq_simul_fator_alt_p bigint, ie_item_simul_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cnae_w			bigint;
ds_cnae_w			varchar(255);
qt_registros_w			bigint;
nr_seq_simul_fator_alt_w	bigint;
i_w				bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_cnae
	from	pls_cnae
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_situacao		= 'A';


BEGIN
i_w	:= 0;
if (ie_item_simul_p	= 'C') then
	open C01;
	loop
	fetch C01 into
		nr_seq_cnae_w,
		ds_cnae_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		i_w	:= i_w + 1;
		select	count(*)
		into STRICT	qt_registros_w
		from	pls_simul_fator_alteracao
		where	nr_seq_tipo_regra	= nr_seq_simul_fator_alt_p
		and	nr_seq_cnae		= nr_seq_cnae_w;

		if (qt_registros_w	= 0) then

			select	nextval('pls_simul_fator_alteracao_seq')
			into STRICT	nr_seq_simul_fator_alt_w
			;

			insert into pls_simul_fator_alteracao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					cd_estabelecimento,nr_seq_tipo_regra,ds_fator_alteracao,nr_seq_cnae)
			values (	nr_seq_simul_fator_alt_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					cd_estabelecimento_p,nr_seq_simul_fator_alt_p,ds_cnae_w,nr_seq_cnae_w);
		else
			update	pls_simul_fator_alteracao
			set	ds_fator_alteracao	= ds_cnae_w
			where	nr_seq_tipo_regra	= nr_seq_simul_fator_alt_p
			and	nr_seq_cnae		= nr_seq_cnae_w;
		end if;

		if (i_w	= 300) then
			commit;
			i_w	:= 0;
		end if;
		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
