CREATE OR REPLACE PROCEDURE pls_carregar_municipio_uf ( sg_uf_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_municipio_ibge_w	varchar(10);
qt_registros_w		bigint;
ie_w			bigint;

C01 CURSOR FOR
	SELECT	cd_municipio_ibge
	from	sus_municipio
	where	ds_unidade_federacao	= sg_uf_p;


BEGIN

qt_registros_w	:= 0;

open C01;
loop
fetch C01 into
	cd_municipio_ibge_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_municipio_atuacao
	where	cd_municipio_ibge	= cd_municipio_ibge_w;

	if (qt_registros_w	= 0) then

		ie_w	:= ie_w + 1;

		insert into pls_municipio_atuacao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				cd_estabelecimento,cd_municipio_ibge)
		values (	nextval('pls_municipio_atuacao_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				cd_estabelecimento_p,cd_municipio_ibge_w);

		if (ie_w = 100) then
			ie_w	:= 0;
			commit;
		end if;
	end if;

	end;
end loop;
close C01;

commit;

end;
