CREATE OR REPLACE PROCEDURE pls_carrega_sca_destino ( nr_seq_sca_p bigint, ie_tipo_acao_p text, ie_opcao_p text, nr_seq_vinculo_p bigint, nr_seq_tabela_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*
ie_tipo_acao_p
I - Inserir sca existentes
E - Inserir sca novas
D - Deletar sca existente
*/
/*
ie_opcao_p
SP - Simulação de Preço
BP - Beneficiário de plano de saúde
BE - Beneficiários de plano de saúde(contatos)
CO- Contratos de plano de saúde
PL - Plano
*/
nr_seq_sca_w		bigint;
nr_sequencia_w		bigint;
qt_registro_w		bigint;
nr_seq_tabela_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_segurado_simul	=  nr_seq_vinculo_p
	and	ie_opcao_p =  'SP'
	
union

	SELECT	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_benef_proposta	 = nr_seq_vinculo_p
	and	ie_opcao_p = 'BP'
	
union

	select	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_vinculo
	where	nr_seq_segurado	=   nr_seq_vinculo_p
	and	ie_opcao_p = 'BE'
	
union

	select	nr_seq_plano nr_seq_sca,
		nr_seq_tabela
	from	pls_sca_regra_contrato
	where	nr_seq_contrato	=  nr_seq_vinculo_p
	and	ie_opcao_p = 'CO'
	
union

	select	nr_seq_plano_adic nr_seq_sca,
		nr_seq_tabela
	from	pls_plano_servico_adic
	where	nr_seq_plano	=  nr_seq_vinculo_p
	and	ie_opcao_p = 'PL';



BEGIN

if (ie_tipo_acao_p	= 'I') then
	delete	FROM w_pls_localizar_sca;
end if;

if (coalesce(nr_seq_tabela_p,0) = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_tabela_w
	from	pls_tabela_preco
	where	nr_seq_plano   = nr_seq_sca_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(nr_segurado::text, '') = ''
	and	((ie_opcao_p = 'BP' AND ie_proposta_adesao = 'S') or
		 (ie_opcao_p = 'SP' AND ie_simulacao_preco = 'S') or (ie_opcao_p not in ('BP','SP')));
else
	nr_seq_tabela_w	:= nr_seq_tabela_p;
end if;

if (ie_tipo_acao_p = 'I') then
	open C01;
	loop
	fetch C01 into
		nr_seq_sca_w,
		nr_seq_tabela_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_opcao_p	= 'BE') then
			insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_sca,nr_seq_segurado,cd_estabelecimento,nr_seq_tabela)
			values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_sca_w,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
		elsif (ie_opcao_p	= 'BP') then
			insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_sca,nr_seq_segurado_prop,cd_estabelecimento,nr_seq_tabela)
			values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_sca_w,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
		elsif (ie_opcao_p	= 'SP') then
			insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_sca,nr_seq_simulacao,cd_estabelecimento,nr_seq_tabela)
			values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_sca_w,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
		elsif (ie_opcao_p	= 'CO') then
			insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_sca,nr_seq_contrato,cd_estabelecimento,nr_seq_tabela)
			values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_sca_w,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
		elsif (ie_opcao_p	= 'PL') then
			insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					nr_seq_sca,nr_seq_plano,cd_estabelecimento,nr_seq_tabela)
			values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_sca_w,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
		end if;
		end;
	end loop;
	close C01;
elsif (ie_tipo_acao_p	= 'E') then
	if (ie_opcao_p	= 'BE') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	w_pls_localizar_sca
			where	nr_seq_sca	= nr_seq_sca_p
			and	nr_seq_segurado		= nr_seq_vinculo_p;

			if (qt_registro_w = 0) then
				insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_sca,nr_seq_segurado,cd_estabelecimento,nr_seq_tabela)
				values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_sca_p,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
			elsif (qt_registro_w > 0) then
				update	w_pls_localizar_sca
				set	nr_seq_tabela = nr_seq_tabela_w
				where	nr_seq_sca	= nr_seq_sca_p
				and	nr_seq_segurado		= nr_seq_vinculo_p;
			end if;
		end if;
	elsif (ie_opcao_p	= 'BP') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
			into STRICT	qt_registro_w
			from	w_pls_localizar_sca
			where	nr_seq_sca	= nr_seq_sca_p
			and	nr_seq_segurado_prop		= nr_seq_vinculo_p;

			if (qt_registro_w = 0) then
				insert into w_pls_localizar_sca(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_sca,nr_seq_segurado_prop,cd_estabelecimento,nr_seq_tabela)
				values (	nextval('w_pls_localizar_sca_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_sca_p,nr_seq_vinculo_p,cd_estabelecimento_p,nr_seq_tabela_w);
			elsif (qt_registro_w > 0) then
				update	w_pls_localizar_sca
				set	nr_seq_tabela = nr_seq_tabela_w
				where	nr_seq_sca	= nr_seq_sca_p
				and	nr_seq_segurado_prop		= nr_seq_vinculo_p;
			end if;
		end if;
	elsif (ie_opcao_p	= 'SP') then
		if (nr_seq_sca_p IS NOT NULL AND nr_seq_sca_p::text <> '') then
			select	count(*)
